	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (281,104,'Сложение +, вычитание -, умножение *, деление /, остаток %.<br />Применинимы ко всем примитивным типам, кроме boolean.<br /><br />+ и += также применимы к String.',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (105,4,0,'Остаток (%) можно брать не только от деления положительных целых чисел, но и от отрицательных, а также от вещественных. Например, остаток от деления отрицательного целого <strong>x</strong> на положительное целое <strong>y</strong> лежит в диапазоне (-y +1)..0 (на экзамене OCA I не требуется).',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
