INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (118,4,0,'Операторы сокращенного присваивания (+=, -=, *= и т.д.) называются <em>compound assignment operators</em>. На экзамене OCA потребуется знание только первых двух.',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
