INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (121,4,0,'В операторах сравнения (relational operators, &lt;,&lt;=,&gt;,&gt;=) идет преобразование типов аналогично бинарным арифметическим операциях',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (122,4,0,'Оператор <strong>instanceof</strong> тоже считается оператором сравнения (relational).',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (123,4,2,'Перечислите логические <span style="text-decoration: underline;">бинарные</span> операторы Java и типы операндов, к которым они применяются.',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (306,123,'Логическое "и": &amp;<br />Логическое "или" (включающее, inclusive): <strong>|</strong><br />Логическое "или" (исключающее, exclusive): <strong>^</strong><br /><br />Применяются к булевым операндам, а также к числовым (в этом случае называются <em>bitwise operators</em>, в OCA не рассматриваются)',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (124,4,0,'Одно из достоинств условных операторов короткого действия, помимо улучшения производительности, заключается в удобстве проверки на null:<br />if(x != null &amp;&amp; x.getValue() &lt; 5) {<br />&nbsp; // Do something<br />}<br /><br />Если бы в вышеприведенном примере кода использовался обычный оператор &amp;, то выдавалась бы Runtime-ошибка NullPointerException',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (125,4,2,'Каков вывод следующего фрагмента кода:<br /><br />int x = 6;<br />boolean y = (x &gt;= 6) || (++x &lt;= 7);<br />System.out.println(x);',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
