INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (126,4,0,'Операторы проверки равенства (equality operators, !=, ==) ведут к преобразованию числовых операндов аналогично арифметическим бинарным операторам.',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (127,4,0,'В операторах проверки равенства нельзя смешивать разные типы операндов. Если сравниваются объекты, то они должны быть из одной иерархии наследования.<br /><br />boolean x = true == 3; // DOES NOT COMPILE<br />boolean y = false != "Giraffe"; // DOES NOT COMPILE<br />boolean z = 3 == "Kangaroo"; // DOES NOT COMPILE<br /><br />String s1 = "java";<br />StringBuilder s2 = new StringBuilder("java");<br />boolean s = s1 == s2; //DOES NOT COMPILE<br /><br /><br />Следует обращать пристальное внимание на типы данных, когда в экзаменационной задаче присутствует оператор проверки равенства.',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (128,4,1,'Какой результат получится в результате выполнения кода:<br /><br />boolean y = false;<br />boolean x = (y = true);<br />System.out.println(x);',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
