INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (181,4,2,'Какая разница между двумя строками?<br /><br />1) String name = "Fluffy";<br />2) String name = new String("Fluffy");',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
