INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (211,4,2,'Каким будет результат следующего кода?<br /><br />String x = "Hello World";<br />String z = " Hello World".trim();<br />System.out.println(x == z);',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
