INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (336,4,0,'Доступ к членам экземпляра отсутствует из static-методов:<br /><br />public class Static {<br />&nbsp;private String name = "Static class";<br />&nbsp;public static void first() { }<br />&nbsp;public static void second() { }<br />&nbsp;public void third() { System.out.println(name); }<br />&nbsp;public static void main(String args[]) {<br />&nbsp;&nbsp;first();<br />&nbsp;&nbsp;second();<br />&nbsp;&nbsp;third(); // DOES NOT COMPILE<br />&nbsp;} <br />}<br /><br />а вот доступ к статичным членам из методов экземпляра имеется:<br /><br />public class Test {<br />&nbsp;&nbsp;&nbsp; public static void main(String... args) {}<br />&nbsp;&nbsp;&nbsp; <br />&nbsp;&nbsp;&nbsp; public void test() {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; main("asd");<br />&nbsp;&nbsp;&nbsp; }<br />}',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (337,4,0,'Обычный пример использования статичных полей - подсчет числа экземпляров.<br /><br />public class Counter {<br />&nbsp; private static int count;<br />&nbsp; public Counter() { count++; }<br />}',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
