INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (359,4,0,'Конструктор с модификатором private используется, когда в классе имеются только статичные методы (создание экземпляров не нужно) или когда создание экзмепляров должно контролироваться только самим классом.',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (360,4,2,'Скомпилируется ли следующий код?<br /><br />public class Hamster {<br />&nbsp;&nbsp;&nbsp; public Hamster(int weight) { <br />&nbsp;&nbsp;&nbsp;&nbsp; Hamster(weight, "brown");<br />&nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp; public Hamster(int weight, String color) { }<br />}',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (786,360,'Не скомпилируется. Конструктор нельзя вызывать подобно методу. Имя конструктора может идти только за ключевым словом <strong>new</strong>.',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (361,4,2,'Скомпилируется ли следующий код?<br /><br />public class Hamster {<br />&nbsp;&nbsp;&nbsp; public Hamster(int weight) {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; new Hamster(weight, "brown");<br />&nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp; public Hamster(int weight, String color) { }<br />}',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (787,361,'скомпилируется, но смысла большого в себе не несет',true,'');
