INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (560,8,2,'Каков смысл и предназначение аннотации <strong>@Override</strong>?',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (1289,560,'Эта аннотация не дает случайно ошибиться и перезагрузить метод вместо переопределения.<br /><br />Если&nbsp;в родительском классе интерфейсе нет метода с данной сигнатурой, то добавление @Overriden приведет к ошибке компиляции.',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (561,8,0,'Методы toString(), equals(), hashCode() класса Object предполагаются для переопределения в большинстве реализуемых пользовательских классов (особенно JavaBean)',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (562,8,0,'<strong>String</strong> переопределяет метод <strong>equals</strong>(), в то время как <strong>StringBuilder</strong> использует версию <strong>Object</strong>.',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (563,8,2,'Приведите пример реализации элементарного <strong>equals</strong> (без hashCode) для класса:<br /><br />public class Lion {<br />&nbsp;&nbsp;&nbsp; private int idNumber;<br />&nbsp;&nbsp;&nbsp; public Lion(int idNumber) {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; this.idNumber = idNumber;<br />&nbsp;&nbsp;&nbsp; }<br />}',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (1290,563,'@Override public boolean equals(Object obj) {<br />&nbsp;&nbsp;&nbsp; if (!(obj instanceof Lion)) return false;<br />&nbsp;&nbsp;&nbsp; Lion otherLion = (Lion) obj;<br />&nbsp;&nbsp;&nbsp; return this.idNumber == otherLion.idNumber;<br />}',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (564,8,2,'Приведите основные правила ("контракт"), которым должен следовать грамотно написанный метод&nbsp; <strong>equals</strong>().',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (1291,564,'1) <strong>Рефлексивность</strong>: x.equals(x) == true<br /><strong><br /></strong>2)<strong> Симметричность</strong>: x.equals(y) == y.equals(x)<br /><strong><br /></strong>3)<strong> Транзитивность</strong>: Если x.equals(y) == y.equals(z), то x.equals(z)<br /><strong><br /></strong>4)<strong> Консистентность</strong>: x.equals(y) возвращает одно и то же значение независимо от обстоятельств и количества проверок на равенство (<em>при условии, что свойства объектов, используемые в equals, не изменились</em>).<br /><br />5) x.equals(null) должно всегда давать false. (<em>а не продуцировать NPE</em>)',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (565,8,2,'Что плохо в этом методе equals?<br /><br />public boolean equals(Lion obj) {<br />&nbsp;&nbsp;&nbsp; if (obj == null) return false;<br />&nbsp;&nbsp;&nbsp; return this.idNumber == obj.idNumber;<br />}',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (1292,565,'неверная сигнатура. Этот метод не переопределяет Object.equals()',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (566,0,0,'Для быстрого написания <strong>equals</strong>() можно использовать EqualsBuilder из ApacheCommons:<br /><br />public boolean equals(Object obj) {<br />&nbsp;&nbsp;&nbsp; return EqualsBuilder.reflectionEquals(this, obj);<br />}<br /><br />Если сравнение нужно только по некоторым полям, то делается так:<br /><br />public boolean equals(Object obj) {<br />&nbsp;&nbsp;&nbsp; if ( !(obj instanceof Lion)) return false;<br />&nbsp;&nbsp;&nbsp; Lion other = (Lion) obj;<br />&nbsp;&nbsp;&nbsp; return new EqualsBuilder().appendSuper(super.equals(obj))<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; .append(idNumber, other.idNumber)<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; .append(name, other.name)<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; .isEquals();<br />}<br /><br />Выглядит громоздко, зато не надо заморачиваться с обработкой null у объектных полей.<br /><br />NB! appendSuper - если надо обеспечить проверку и методом equals вышестоящего класса.',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (567,8,0,'Если переопределяем <strong>equals</strong>(), надо переопределить и <strong>hashCode</strong>()',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (568,8,2,'Для чего нужен hashCode?',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (1293,568,'Хеш-код - это число, которое можно рассчитать для некоторого экземпляра некоторого класса и которое позволяет группировать экземпляры класса по некоторому конечному числу категорий - с тем, чтобы легче было искать нужный экземпляр при работе с хешированными структурами.',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (569,8,0,'Поля экземпляра типов <strong>boolean</strong> и <strong>char</strong> обычно не включают в расчет <strong>hashCode</strong>().',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (570,8,2,'Каковы правила имплементации ("контракт") метода <strong>hashCode</strong>()?',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (1294,570,'<strong>1</strong>. Внутри программы результат <strong>hashCode</strong>() не должен меняться (т.е. у экземпляра не должны меняться поля, которые участвуют в расчете <strong>hashCode</strong>())<br /><strong>2</strong>. Если два объекта равны друг другу по <strong>equals</strong>(), их <strong>hashCode</strong>() также должен быть равным. (т.е. в расчете <strong>hashCode</strong>() должно участвовать подмножество полей, используемых в <strong>equals</strong>()).<br /><strong>3</strong>. Если два объекта не равны по <strong>equals</strong>(), <strong>hashCode</strong>() <span style="text-decoration: underline;">не обязан</span> быть отличным.',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (571,8,1,'Какие имплементации <strong>hashCode</strong>() из нижеперечисленных валидны?<br /><br />public class Lion {<br />&nbsp;&nbsp;&nbsp; private int idNumber;<br />&nbsp;&nbsp;&nbsp; private int age;<br />&nbsp;&nbsp;&nbsp; public Lion(int idNumber, int age) {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; this.idNumber = idNumber;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; this.age = age;<br />&nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp; public boolean equals(Object obj) {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if ( !(obj instanceof Lion)) return false;<br />&nbsp;&nbsp; &nbsp; &nbsp;&nbsp; Lion otherLion = (Lion) obj;<br />&nbsp;&nbsp; &nbsp; &nbsp;&nbsp; return this.idNumber == otherLion.idNumber;<br />&nbsp;&nbsp;&nbsp; }<br />}',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (1296,571,'public long hashcode() { return idNumber; }',false,'неверный тип возвращаемого значения (не скомпилируется), плюс hashcode неправильно написано');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (1297,571,'public int hashCode() { return 6; }',true,'плохо, но по контракту допустимо');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (1298,571,'public int hashCode() { return idNumber * 7 + age; }',false,'метод опирается на поле <strong>age</strong>, которое не используется в <strong>equals</strong>(),');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (1295,571,'public int hashCode() { return idNumber; }',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (572,8,2,'Какой самый простой способ написать хороший <strong>hashCode</strong>()?',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (1299,572,'сложить хешкоды полей, предварительно умножая их на некоторое заранее выбранное простое число.',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (573,8,2,'Что такое <em>enum</em> в Java?',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (1300,573,'<em>Enum</em>, или<em> перечислимый тип </em>- это класс, реализующий энумерацию, т.е. фиксированный набор констант. Преимущество этого типа в том, что любые ошибки в использовании констант отслеживаются на уровне компиляции.',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (574,8,2,'Приведите синтаксис определения перечислимого типа, а также присванивание переменной',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (1301,574,'public enum Season {<br />&nbsp;&nbsp;&nbsp; WINTER, SPRING, SUMMER, AUTUMN[;]<br />}<br />...<br />Season s = Season.WINTER;<br />(заглавные буквы в константах enum - это не требование компилятора, а соглашение)',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (575,8,0,'Экземпляры enum - это, фактически, статичные финальные константы.',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (576,8,0,'Основной API перечислимых типов.<br /><br /><strong>1</strong>. Статический метод <strong>values</strong>(), который возвращает <span style="text-decoration: underline;">массив</span> всех экзампляров типа:<br />Season[] ss = Season.values();<br /><br /><strong>2</strong>. Метод экземпляра <strong>ordinal</strong>(), возвращающий порядковый номер элемента в перечислении.<br /><br /><strong>3</strong>. Статический метод <strong>valueOf</strong>(), преобразующий строку в экземпляр типа:<br />Season s = Season.valueOf("SUMMER");',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (577,8,0,'Перечислимый тип нельзя дополнить новыми элементами в runtime. Также невозможно наследование от перечислимого типа.',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (578,8,2,'Приведите пример использования перечислимого типа в конструкции <strong>switch</strong>.',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (1302,578,'Season summer = Season.SUMMER;<br />switch(summer) {<br />&nbsp;&nbsp;&nbsp; case WINTER:<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ...<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; break;<br />&nbsp;&nbsp;&nbsp; case SUMMER:<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ...<br />}',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (579,8,0,'В перечислимых типах можно определять конструкторы, переменные и методы экземпляра:<br /><br />public enum Season {<br />&nbsp;&nbsp;&nbsp; WINTER("Low"), SPRING("Medium"), SUMMER("High"), FALL("Medium");<br />&nbsp;&nbsp;&nbsp; private String expectedVisitors;<br />&nbsp;&nbsp;&nbsp; private Season(String expectedVisitors) {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; this.expectedVisitors = expectedVisitors;<br />&nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp; public void printExpectedVisitors() {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; System.out.println(expectedVisitors);<br />&nbsp;&nbsp;&nbsp; } <br />}<br /><br />Внимание! 1) Список значений идет первым. 2) Двоеточие после списка становится обязательным, если кроме списка есть еще что-то. 3) Конструктор может быть только private или вообще без модификатора доступа (private по факту).',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (580,8,0,'Конструкторы перечислимого типа выполняются в момент первого упоминания типа в коде.',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (581,8,0,'Члены&nbsp;экземпляра перечислимого типа могут маркироваться <strong>protected</strong>, хотя особого смысла это не имеет - по факту равно <strong>default</strong>.',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (582,8,0,'<p>В перечислимом типе возможно переопределить метод экземпляра на уровне каждого элемента списка:<br /><br />enum Season {<br />&nbsp;&nbsp;&nbsp; WINTER {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; public void test2() {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; System.out.println("test2!!!!");<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp; }, SPRING, SUMMER, AUTUMN;<br />&nbsp;&nbsp;&nbsp; <br />&nbsp;&nbsp;&nbsp; <br />&nbsp;&nbsp;&nbsp; public&nbsp; void test2(){<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <br />&nbsp;&nbsp;&nbsp; }</p>
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (583,8,2,'Перечислите типы вложенных классов в Java.',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (1303,583,'1) Внутренний класс экземпляра (member inner class, или просто inner class) - определен на уровне членов экземпляра.<br />2) Локальный внутренний класс (local inner class) - определяется внутри метода.<br />3) Анонимный внутренний класс (anonymous inner class) - особая разновидность локального внутреннего класса, не имеет имени.<br />4) Статический вложенный класс (static nested class) - определен на уровне статических членов.',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (584,8,2,'Перечислите основные преимущества использования внутренних классов в Java.',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (1304,584,'- инкапсуляция вспомогательных классов внутри конкретного класса (ограничение доступа)<br />- создание "одноразовых" классов<br />- создание более удобочитаемого кода',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (585,8,2,'Укажите формальные свойства внутренних классов-членов (<em>member inner classes</em>):',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (1305,585,'<strong>1</strong>. Могут иметь любые модификаторы доступа<br /><strong>2</strong>. Могут наследовать любому классу и реализовывать любой интерфейс.<br /><strong>3</strong>. Могут быть абстрактными или финальными (или не быть таковыми).<br /><strong>4</strong>. НЕ МОГУТ объявлять статические поля или методы (за исключением финальных статичных полей).<br /><strong>5</strong>. Имеют доступ ко всем членам своего внешнего класса, включая приватные.',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (586,8,0,'Перечислимые типы могут иметь статические переменные и методы!',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (587,8,0,'Внутренние классы экземпляра имеют непосредственный доступ без каких-либо префиксов к членам своего внешнего класса (как статическим, так и экземпляра). Префикс <strong>this</strong> в этом случае использовать нельзя - он&nbsp; только для членов самого внутренного класса.',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (588,8,2,'Напишите простейший код создания экземпляра внутреннего класса во внешнем.',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (1306,588,'class Outer {<br />&nbsp;&nbsp;&nbsp; class Inner {}<br />&nbsp;&nbsp;&nbsp; public void go() {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Inner in = new Inner();<br />&nbsp;&nbsp;&nbsp; }<br />}',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (589,8,2,'Создайте экземпляр внутреннего класса (member inner class) с помощью объекта внешнего.',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (1307,589,'Outer outer = new Outer();<br />Inner inner = outer.new Inner();',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (590,8,0,'Для создания внутреннего (member inner) класса необходим <span style="text-decoration: underline;">экземпляр</span> внешнего.',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (591,8,0,'Внутренний (member inner) класс может содержать такие же имена полей, как и внешний, в этом случае выбор той или иной переменной контролируется с помощью <strong>this</strong>, но делать так <span style="text-decoration: underline;">не надо</span>.',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (592,8,2,'Есть класс:<br /><br />class Outer {<br />&nbsp;&nbsp;&nbsp; String x = "Outer";<br /><br />&nbsp;&nbsp;&nbsp; class Inner {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; String&nbsp; x = "Inner";<br /><br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; void test() {<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; <br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp; }<br />}<br /><br />1) Как в методе <strong>test</strong>() обратиться к переменной <strong>x</strong> класса Outer?<br />2) Как вне класса Outer создать экземпляр класса Inner?',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (1308,592,'1) Outer.this.x<br /><br />2) Outer.Inner in = new Outer().new Inner();',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (593,8,0,'Если во внутреннем (member inner) классе обращаться к члену экземпляра какого-то из вышестоящих классов по вложенности, то в случае совпадения имен члена в нескольких классах возьмется самый ближний, если идти снизу вверх.',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (594,8,0,'В статических методах внешнего класса можно создавать ссылки напрямую на тип внутреннего (member inner) класса, однако для этого все равно потребуется экземпляр внешнего:<br /><br />class Outer {<br />&nbsp;&nbsp;&nbsp; <br />&nbsp;&nbsp;&nbsp; class Inner {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <br />&nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp; <br />&nbsp;&nbsp;&nbsp; static void test() {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; [Outer.]Inner in2 = new Outer().new Inner();<br />&nbsp;&nbsp;&nbsp; }<br />}',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (595,8,0,'Внутренним (member inner) может быть и интерфейс:<br /><br />public class CaseOfThePrivateInterface {<br />&nbsp;&nbsp;&nbsp; private interface Secret {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; public void shh();<br />&nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp; class DontTell implements Secret {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; public void shh() { }<br />&nbsp;&nbsp;&nbsp; } <br />}',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (596,8,2,'Что такое "локальный внутренний класс" (local inner class)?',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (1309,596,'Локальный внутренний класс - вложенный класс, определенный внутри <span style="text-decoration: underline;">метода</span>. Класс существует только внутри метода, объявляется только при вызове метода и выходит за пределы области видимости при выходе из метода. Однако экземпляры таких классов можно вернуть из метода.',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (597,8,2,'Перечислите свойства локальных внутренних (local inner) классов.',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (1310,597,'<strong>1</strong>. Не могут иметь модификатора доступа.<br /><strong>2</strong>. Не могут быть объявлены статическими и не могут иметь статических членов (за исключением статических финальных полей).<br /><strong>3</strong>. Имеют доступ ко всем полям и методам окружающего класса.<br /><strong>4</strong>. Не имеют доступа к локальным переменным метода, за исключением финальных либо "эффективно финальных", причем переменная должна получить значение ДО объявления класса.',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (598,8,2,'В чем состоит концепция "эффективно финальных" локальных переменных, введенная в Java 8?',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (1311,598,'"Эффективно финальной" считается локальная переменная, к объявлению которой можно добавить модификатор <strong>final</strong> без возникновения ошибки компиляции.',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (599,8,1,'Отметьте все&nbsp;"эффективно финальные" локальные переменные нижеприведенного кода.<br /><br />public void isItFinal() {<br />&nbsp;&nbsp;&nbsp; int one = 20;<br />&nbsp;&nbsp;&nbsp; int two = one;<br />&nbsp;&nbsp;&nbsp; two++;<br />&nbsp;&nbsp;&nbsp; int three;<br />&nbsp;&nbsp;&nbsp; if ( one == 4) three = 3;<br />&nbsp;&nbsp;&nbsp; else three = 4;<br />&nbsp;&nbsp;&nbsp; int four = 4;<br />&nbsp;&nbsp;&nbsp; class Inner { }<br />&nbsp;&nbsp;&nbsp; four = 5;<br />}',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (1312,599,'one',true,'присваивается единственный раз в строке объявления');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (1313,599,'two',false,'значение меняется после присваивания (инкремент)');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (1314,599,'three',true,'присваивается единственный раз в альтернативе');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (1315,599,'four',false,'значение меняется после присваивания (то, что это происходит ПОСЛЕ объявления локального внутреннего класса, ситуацию не меняет)');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (600,8,2,'Что такое "анонимный внутренний (anonymous inner) класс"?',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (1316,600,'Анонимный внутренний класс - это локальный внутренний класс без имени. Он создается с помощью ключевого слова <strong>new</strong> и в обязательном порядке наследует существующему классу либо реализует существующий интерфейс. Анонимные классы нужны, чтобы быстро инстанциировать "одноразовый" экземпляр.',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (601,8,2,'Приведите простой пример инстанциирования анонимного внутреннего класса.',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (1317,601,'&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Object obj = new Object() {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; public String toString() {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return&nbsp; "obj" + super.toString();<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; };',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (602,8,0,'Анонимный внутренний класс не может одновременно наследовать классу (кроме Object) и реализовывать интерфейс. В этом случае можно использовать локальный внутренний класс.',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (603,8,0,'Анонимный внутренний класс можно использовать даже в качестве аргумента метода!<br /><br />public int pay() {<br />&nbsp;&nbsp;&nbsp; return admission(5, new SaleTodayOnly() {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; public int dollarsOff() { return 3; }<br />&nbsp;&nbsp;&nbsp; });<br />}',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (604,8,0,'Анонимные внутренние классы очень часто используются в построении графических интерфейсов. Например в качестве листенера удобно подать экземпляр анонимного класса, который имеет доступ к экземпляру класса, в котором он был создан:<br /><br />JButton button = new JButton("red");<br />button.addActionListener(new ActionListener() {<br />&nbsp;&nbsp;&nbsp; public void actionPerformed(ActionEvent e) {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; //handle the button click<br />&nbsp;&nbsp;&nbsp; }<br />});',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (605,8,2,'Что такое вложенный (static nested) класс?',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (1318,605,'Вложенный класс - это класс, определенный как статический член класса. Для инстанциирования такому классу не нужен объект внешнего класса, т.е. доступа к полям экземпляра внешнего класса напрямую нет.',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (606,8,2,'Перечислите особенности вложенных (static nested) классов.',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (1319,606,'<strong>1</strong>. Для обращения к вложенному классу извне требуется имя внешнего класса<br /><strong>2</strong>. У вложенного класса могут быть любые модификаторы доступа.<br /><strong>3</strong>. Внешний класс имеет доступ ко всем членам вложенного класса, а вложенный - ко всем членам внешнего. Для обращения к членам экземпляра понадобятся ссылки на объекты классов. Статические члены внешнего класса видны в коде вложенного напрямую.',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (607,8,2,'Приведите простой пример инстанциирования вложенного класса <br />1) внутри внешнего<br />2) извне',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (1320,607,'1)<br />class Enclosing {<br />&nbsp;&nbsp;&nbsp; static class Nested {}<br />&nbsp;&nbsp;&nbsp; public static void main(String[] args) {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Nested nested = new Nested();<br />&nbsp;&nbsp;&nbsp; } <br />}<br /><br />2)<br />class Enclosing {<br />&nbsp;&nbsp;&nbsp; static class Nested {}<br />}<br />class Outer {<br />&nbsp;&nbsp;&nbsp; public void instantiate() {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Enclosing.Nested nested = new Enclosing.Nested();<br />&nbsp;&nbsp;&nbsp; }<br />}',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (608,8,2,'Как импортировать класс Beak, определенный в следующем коде?<br /><br />package bird;<br /><br />public class Toucan {<br />&nbsp;&nbsp;&nbsp; public static class Beak {}<br />}',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (1321,608,'Два способа.<br /><br />1) обычным импортом:<br />import bird.Toucan.Beak;<br /><br />2) статическим импортом:<br />import static bird.Toucan.Beak;',true,'');
