INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (640,8,0,'Лямбду можно записывать сокращенно.<br /><br />Полная версия:<br />(Animal a) -&gt; { return a.canHop(); }<br /><br />Сокращенная:<br />a -&gt; a.canHop()<br /><br />Сокращенную лямбду можно подавать в тот контекст, в котором становятся ясными опущенные параметры - в данном случае, тип аргумента.',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
