	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (1477,648,'Предикатный интерфейс java.util.function.Predicate предназначен для использования с лямбдами и выглядит так:<br /><br />public interface Predicate&lt;T&gt; {<br />&nbsp;&nbsp;&nbsp; public boolean test(T t);<br />}<br /><br />Благодаря наличию предикатного интерфейса нет необходимости создавать свой собственный для лямбд с единственным параметром и boolean-методом.',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (649,8,0,'<p>Простой пример использования предикатного интерфейса. Программа, определяющая четность произвольного числа (обернутого во вспомогательный класс <strong>MyNumber</strong>).<br /><br />public class TestNumber {<br />&nbsp;&nbsp;&nbsp; <br />&nbsp;&nbsp;&nbsp; public static void main(String args[]) {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; MyNumber num = new MyNumber(1.5);<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; System.out.println(testNumOdd(num, n -&gt; n.isOdd()));<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; num = new MyNumber(15L);<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; System.out.println(testNumOdd(num, n -&gt; n.isOdd()));<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; num = new MyNumber((byte)113);<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; System.out.println(testNumOdd(num, n -&gt; n.isOdd()));<br />&nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp; <br />&nbsp;&nbsp;&nbsp; static boolean testNumOdd(MyNumber num, Predicate&lt;MyNumber&gt; testOdd) {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return testOdd.test(num);<br />&nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp; <br />}</p>
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (650,8,0,'Полиморфизм - это, по сути, способность одного и того же реального объекта выступать в разных ролях, т.е. подаваться в контексты, где ожидается любой из суперклассов или интерфейсов, реализуемых/наследуемых данным объектов.',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
