INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (719,8,0,'Необходимо помнить, что дженерики сильны на этапе компиляции, поэтому не надо думать "как Java поймет, какой тип передался", т.к. Java над этим не думает и работает только с объектами, а все необходимые кастинги компилятор сам добавляет в JVM, исходя из контекста кода.',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (720,8,2,'Как явным образом передать в дженерик-метод желаемый тип?',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (1624,720,'Указать его в угловых скобках перед именем метода, однако метод должен обязательно идти после dot-оператора.<br /><br />new Test4().&lt;String&gt;getTT("d");<br /><br />В этом случае компилятор проверит аргумент на соответствие подаваемому типу, а если бы этого типа не было, то компилятор использовал бы тип параметра как подаваемый тип.',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (721,8,0,'Использование параметризованных классов без параметризации (<em>raw types</em>) опасно, т.к.&nbsp;ссылку такого типа можно без проблем подать в метод, который ожидает конкретного дженерика, и в методе будут проблемы.<br /><br />class Dragon {}<br />class Unicorn { }<br />public class LegacyDragons {<br />&nbsp;&nbsp;&nbsp; public static void main(String[] args) {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; List unicorns = new ArrayList();<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; unicorns.add(new Unicorn());<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; printDragons(unicorns);<br />&nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp; private static void printDragons(List&lt;Dragon&gt; dragons) {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; for (Dragon dragon: dragons) { // ClassCastException<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; System.out.println(dragon);<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; } <br />&nbsp;&nbsp;&nbsp; } <br />}',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
