INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (723,8,2,'Почему и в какой строке будет выдана ошибка при выполнении следующего кода?<br /><br />-1: class Dragon {}<br />0: class Unicorn { }<br /><br />1: public class LegacyUnicorns {<br />2:&nbsp;&nbsp;&nbsp;&nbsp; public static void main(String[] args) {<br />3:&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; java.util.List&lt;Unicorn&gt; unicorns = new java.util.ArrayList&lt;&gt;();<br />4:&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; addUnicorn(unicorns);<br />5:&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Unicorn unicorn = unicorns.get(0);<br />6:&nbsp;&nbsp;&nbsp;&nbsp; }<br />7:&nbsp;&nbsp;&nbsp;&nbsp; private static void addUnicorn(java.util.List unicorn) {<br />8:&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; unicorn.add(new Dragon());<br />9:&nbsp;&nbsp;&nbsp;&nbsp; } <br />10: }',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (1625,723,'Ошибка будет в строке 5, <strong>ClassCastException</strong>. Она произойдет из-за того, что метод <strong>addUnicorn</strong> принимает <em>raw</em>-список (которым оказывается при подаче в метод&nbsp;"корректный" массив <em>unicorns</em>) и помещает туда произвольный объект. Потом при попытке извлечь объект конкретного типа (согласно ссылке, с которой работаем) выясняется, что там вовсе не <strong>Unicorn</strong>.',true,'');
