INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (894,8,2,'Что напечатает следующий код?<br /><br />Stream&lt;Integer&gt; infinite = Stream.iterate(1, x -&gt; x + 1);<br />infinite.filter(x -&gt; x % 2 == 1)<br />&nbsp; .peek(System.out::print)<br />&nbsp; .limit(5)<br />&nbsp; .forEach(System.out::print);',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
