INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (918,8,2,'Что выдаст следующий код?<br /><br />List&lt;String&gt; cats = new ArrayList&lt;&gt;();<br />cats.add("Annie");<br />cats.add("Ripley");<br />Stream&lt;String&gt; stream = cats.stream();<br />cats.add("KC");<br />System.out.println(stream.count());',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (1898,918,'<strong>3</strong>. Потоки используют lazy evaluation, так что в строке <br />Stream&lt;String&gt; stream = cats.stream(); <br />полновесный поток реально не создается, а создается объект потока, который знает, куда смотреть, когда потребуются данные.',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (919,8,0,'В классе java.util.<strong>Optional</strong>&lt;<em>T</em>&gt; есть полезные методы:<br /><br />1) &lt;U&gt; Optional&lt;U&gt; <strong>map</strong>(Function&lt;? super T,? extends U&gt; mapper)<br /><br />мапит Optional в Optional потенциально другого типа.<br />Если результат функции null или если исходный контейнер пустой - будет пустой контейнер.<br />Если маппер null, то NullPointerException<br /><br />2) Optional&lt;T&gt; <strong>filter</strong>(Predicate&lt;? super T&gt; predicate)<br /><br />Проверяет содержимое контейнера на соответствие предикату. Если не соответствуют - возвращает пустой контейнер, если соответствует - возвращает контейнер со значением.<br /><br />3) &lt;U&gt; Optional&lt;U&gt; <strong>flatMap</strong>(Function&lt;? super T,Optional&lt;U&gt;&gt; mapper)<br /><br />Метод похож на map, но для мапинга используею функцию, которая возвращает Optional&lt;U&gt;, а не значение.',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (920,8,2,'Напишите метод, который принимает Optional&lt;Integer&gt; и печатает его содержимое, если в числе ровно три цифры.',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (1899,920,'void threeDigit(Optional&lt;Integer&gt; optional) {<br />&nbsp;&nbsp;&nbsp; optional.map(n -&gt; "" + n)<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; .filter(s -&gt; s.length() == 3)<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; .ifPresent(System.out::println);<br />}',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (921,8,0,'Методы, которые выбрасывают checked exceptions, нельзя использовать в лямбдах на основе стандартных функциональных интерфейсов Java, так как последние не могут продуцировать checked exceptions. В этом случае будет ошибка компиляции. Выходов два.<br />1) написать собственный функциональный интерфейс, декларирующий checked exceptions<br />2) написать метод-обертку, в котором checked exception будет преобразовываться в uncheched, и уже этот метод использовать в лямбде.',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (922,8,0,'В Collectors есть коллекторы<br />Collector&lt;T,?,Double&gt; <strong>averagingDouble</strong>(ToDoubleFunction&lt;? super T&gt; f), <br />Collector&lt;T,?,Double&gt; <strong>averagingInt</strong>(ToIntFunction&lt;? super T&gt; f), <br />Collector&lt;T,?,Double&gt; <strong>averagingLong</strong>(ToDoubleFunction&lt;? super T&gt; f)<br /><br />высчитывают среднее по итогам маппинга на примитив.<br /><br />Пример:<br />Stream&lt;String&gt; ohMy = Stream.of("lions", "tigers", "bears");<br />Double result = ohMy.collect(Collectors.averagingInt(String::length));',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (923,8,0,'<a>В Collectors есть коллектор<br /><br />Collector&lt;T,?,Long&gt; <strong>counting</strong>()<br /><br />который считает элементы<br /><br />Пример:<br />Stream&lt;String&gt; ohMy = Stream.of("lions", "tigers", "bears");<br />Long result = ohMy.collect(Collectors.counting());<br /><br /></a>',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (924,8,0,'<a>В Collectors есть коллекторы<br /><br />Collector&lt;CharSequence,?,String&gt; <strong>joining</strong>()<br />Collector&lt;CharSequence,?,String&gt; <strong>joining</strong>(CharSequence delimiter)<br /><br />конкатенация с разделителем или без. порядок сохраняется.<br /><br />Пример:<br /><br />Stream&lt;String&gt; ohMy = Stream.of("lions", "tigers", "bears");<br />String result = ohMy.collect(Collectors.joining(", "));<br /><br /></a>',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (925,8,0,'<a>В Collectors есть коллекторы<br /><br />Collector&lt;T,?,Optional&lt;T&gt;&gt; <strong>minBy</strong>(Comparator&lt;? super T&gt; comparator)<br />Collector&lt;T,?,Optional&lt;T&gt;&gt; <strong>maxBy</strong>(Comparator&lt;? super T&gt; comparator)<br /><br />поиск максимального/минимального элемента согласно компаратору<br /><br />Stream&lt;String&gt; ohMy = Stream.of("lions", "tigers", "bears");<br />Optional&lt;String&gt; result = ohMy.collect(Collectors.minBy(Comparator.naturalOrder()));<br /><br /></a>',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (926,8,0,'<a>В Collectors есть коллекторы<br /><br />Collector&lt;T,?,DoubleSummaryStatistics&gt; <strong>summarizingDouble</strong>(ToDoubleFunction&lt;? super T&gt; mapper)<br />Collector&lt;T,?,IntSummaryStatistics&gt; <strong>summarizingInt</strong>(ToIntFunction&lt;? super T&gt; mapper)<br />Collector&lt;T,?,LongSummaryStatistics&gt; <strong>summarizingLong</strong>(ToLongFunction&lt;? super T&gt; mapper)<br /><br />Возвращают объекты статистики по потоку.<br /><br />Пример:<br /><br />Stream&lt;String&gt; ohMy = Stream.of("lions", "tigers", "bears");<br />IntSummaryStatistics result = ohMy.collect(Collectors.summarizingInt(String::length));<br /><br /><br /><br /></a>',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (927,8,0,'<a>В Collectors есть коллекторы<br /><br />Collector&lt;T,?,Double&gt; <strong>summingDouble</strong>(ToDoubleFunction&lt;? super T&gt; mapper)<br />Collector&lt;T,?,Integer&gt; <strong>summingInt</strong>(ToIntFunction&lt;? super T&gt; mapper)<br />Collector&lt;T,?,Long&gt; <strong>summingLong</strong>(ToLongFunction&lt;? super T&gt; mapper)<br /><br />суммируют элементы по мапперу.<br /><br />Пример:<br /><br />Stream&lt;String&gt; ohMy = Stream.of("lions", "tigers", "bears");<br />int result = ohMy.collect(Collectors.summingInt(String::length));<br /><br /></a>',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (928,8,0,'<a>В Collectors есть коллекторы<br /><br />Сollector&lt;T,?,List&lt;T&gt;&gt; <strong>toList</strong>()<br />Collector&lt;T,?,Set&lt;T&gt;&gt; <strong>toSet</strong>()<br />Collector&lt;T,?,C&gt; <strong>toCollection</strong>(Supplier&lt;C&gt; collectionFactory)<br /><br />Помещают элементы в соответствующую коллекцию. О реализации List и Set мы ничего не знаем и полагаться на их класс, изменяемость, сериализируемость не можем. Если надо полагаться - то используем toCollection.<br /><br />Пример:<br /><br />Stream&lt;Integer&gt; ohMy = Stream.of(1,2,3);<br />List&lt;Integer&gt; result = ohMy.collect(Collectors.toCollection(() -&gt; new ArrayList()));<br />(а вот new ArrayList&lt;Integer&gt;()) не скомпилируется почему-то).<br /><br />или лучше<br /></a><a>List&lt;Integer&gt; result = ohMy.collect(Collectors.toCollection(ArrayList::new));<br /></a>',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (929,8,0,'<a>В Collectors есть коллекторы<br /><br /><strong>1</strong>) Collector&lt;T,?,Map&lt;K,U&gt;&gt; <strong>toMap</strong>(Function&lt;? super T,? extends K&gt; keyMapper, Function&lt;? super T,? extends U&gt; valueMapper)<br /><br />заполняет Map на основе каждого элемента потока. Пример:<br /><br />Stream&lt;String&gt; ohMy = Stream.of("lions", "tigers", "bears");<br />Map&lt;String, Integer&gt; map = ohMy.collect(<br />&nbsp; Collectors.toMap(s -&gt; s, String::length)); // здесь две лямбды - обычная и метод-ссылка<br /><br />Если поданные функции приведут к неуникальности ключей, будет RuntimeException.<br /><br /><strong>2</strong>) Collector&lt;T,?,Map&lt;K,U&gt;&gt; <strong>toMap</strong>(Function&lt;? super T,? extends K&gt; keyMapper, Function&lt;? super T,? extends U&gt; valueMapper, BinaryOperator&lt;U&gt; mergeFunction)<br /><br />То же самое, что и предыдущий, но есть защита от дублированных ключей - в этом случае значения смержатся в соответствии с mergeFunction. Пример: <br /><br />Stream&lt;String&gt; ohMy = Stream.of("lions", "tigers", "bears");<br />Map&lt;Integer, String&gt; map = ohMy.collect(Collectors.toMap(<br />&nbsp; String::length, k -&gt; k, (s1, s2) -&gt; s1 + "," + s2));<br /><br /><strong>3</strong>) Collector&lt;T,?,M&gt; toMap(Function&lt;? super T,? extends K&gt; keyMapper,<br />&nbsp;&nbsp; &nbsp;&nbsp; Function&lt;? super T,? extends U&gt; valueMapper,<br />&nbsp; &nbsp; &nbsp; BinaryOperator&lt;U&gt; mergeFunction,<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Supplier&lt;M&gt; mapSupplier)<br /><br />все как и выше, но позволяет подать нужный объект Map, а не полагаться на решение Java.<br /><br />TreeMap&lt;Integer, String&gt; map = ohMy.collect(Collectors.toMap(<br />&nbsp; String::length, k -&gt; k, (s1, s2) -&gt; s1 + "," + s2, TreeMap::new));<br /></a>',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (930,8,0,'Лямбду-функцию, которая возвращает поданный аргумент (s -&gt; s), можно указать как Function.<strong>identity</strong>()',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (931,8,0,'<a>В Collectors есть коллекторы<br /><br />1) Collector&lt;T,?,Map&lt;K,List&lt;T&gt;&gt;&gt; <strong>groupingBy</strong>(Function&lt;? super T,? extends K&gt; classifier)<br /><br />возвращает Map, где ключом является результат работы функции, а значением - список элементов потока, которые порождают данный результат функции. Пример:<br /><br />Stream&lt;String&gt; ohMy = Stream.of("lions", "tigers", "bears");<br />Map&lt;Integer, List&lt;String&gt;&gt; map = ohMy.collect(<br />&nbsp; Collectors.groupingBy(String::length)); <br />-- {5=[lions, bears], 6=[tigers]}<br /><br />2) Collector&lt;T,?,Map&lt;K,D&gt;&gt; <strong>groupingBy</strong>(Function&lt;? super T,? extends K&gt; classifier, Collector&lt;? super T,A,D&gt; downstream)<br /><br />+ определяет способ организации сгруппированных наборов:<br /><br />Map&lt;Integer, Set&lt;String&gt;&gt; map = ohMy.collect(<br />&nbsp; Collectors.groupingBy(String::length, Collectors.toSet()));<br /><br />3) Collector&lt;T,?,M&gt; <strong>groupingBy</strong>(Function&lt;? super T,? extends K&gt; classifier, Supplier&lt;M&gt; mapFactory, Collector&lt;? super T,A,D&gt; downstream)<br /><br />+ явно подает объект Map: <br /><br />TreeMap&lt;Integer, Set&lt;String&gt;&gt; map = ohMy.collect(<br />&nbsp; Collectors.groupingBy(String::length, TreeMap::new, Collectors.toSet()));<br /></a>',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (932,8,0,'<em>Partitioning</em> - специальный вид группировки. Совокупность разделяется на две группы в зависимости от true или false условия разделения.',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (933,8,0,'<a>В Collectors есть коллекторы<br /><br />1) Collector&lt;T,?,Map&lt;Boolean,List&lt;T&gt;&gt;&gt; <strong>partitioningBy</strong>(Predicate&lt;? super T&gt; predicate)<br /><br />Разделяет элементы поток на две части по предикату и возвращает Map&lt;Boolean List&lt;T&gt;&gt;<br /><br />Stream&lt;String&gt; ohMy = Stream.of("lions", "tigers", "bears");<br />Map&lt;Boolean, List&lt;String&gt;&gt; map = ohMy.collect(<br />&nbsp; Collectors.partitioningBy(s -&gt; s.length() &lt;= 5));<br />-- {false=[tigers], true=[lions, bears]}<br /><br />2) Collector&lt;T,?,Map&lt;Boolean,D&gt;&gt; <strong>partitioningBy</strong>(Predicate&lt;? super T&gt; predicate, Collector&lt;? super T,A,D&gt; downstream)<br /><br />то же самое, но группы складываются не в List, а редуцируются произвольным коллектором:<br /><br />Map&lt;Boolean, Set&lt;String&gt;&gt; map = ohMy.collect(<br />&nbsp; Collectors.partitioningBy(s -&gt; s.length() &lt;= 7, Collectors.toSet()));<br /><br />Метода, позволяющего подать Map, куда складывать результаты, не предусмотрено.<br /></a>',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
