INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (976,8,0,'<p>немного старого API с датой/временем:<br /><br />import java.util.*;<br /><br />1) Текущие дата/время: Date d = new Date();<br />2) дата 01.01.2015<br /><br />Calendar c = Calendar.getInstance();<br />c.set(2015, Calendar.JANUARY, 1);<br />Date jan = c.getTime();<br />&nbsp;&nbsp;&nbsp; <em>или</em><br />Calendar c = new GregorianCalendar(2015, Calendar.JANUARY, 1);<br />Date jan = c.getTime();<br />&nbsp;&nbsp;&nbsp; <em>или</em><br />Calendar c = Calendar.getInstance();<br />c.set(2015, 0, 1);<br />Date jan = c.getTime();<br />&nbsp;</p>',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
