INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (982,8,0,'Еще немного старого API дат и времен.<br /><br />import java.util.*;<br /><br />1) прибавить день<br /><br />Date date = new Date();<br />Calendar cal = Calendar.getInstance();<br />cal.setTime(date);<br />cal.add(Calendar.DATE, 1);<br />Date newDate = cal.getTime();<br /><br />2) отнять день<br /><br />Date date = new Date();<br />Calendar cal = Calendar.getInstance();<br />cal.setTime(date);<br />cal.add(Calendar.DATE, -1);<br />Date newDate = cal.getTime();',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (983,8,0,'<p>У классов даты/времени есть методы, унаследованные из интерфейса java.time.chrono.ChronoZonedDateTime. Это <strong>isBefore</strong>(), <strong>isAfter</strong>(), <strong>isEqual</strong>().<br /><br />Еще есть интересный метод <strong>toInstant</strong>(), который возвращает объект типа Instant, хранящий временной слепок (почитать javadoc при необходимости)</p>',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
