	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (2217,1089,'Концепция действует, начиная с Java 7. Смысл - ловить сразу несколько типов исключений в одной catch-конструкции:<br /><br />try {<br />&nbsp;...<br />} catch (DateTimeParseException | IOException&nbsp;| NumberFormatException&nbsp;e) {<br />&nbsp;&nbsp;&nbsp; e.printStackTrace();<br />&nbsp;&nbsp;&nbsp; throw new RuntimeException(e);<br />}',true,'');
