INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (1134,8,2,'Дайте краткие определения потока (<em>thread</em>), процесса (<em>process</em>), задачи (<em>task</em>)&nbsp;.',TIMESTAMP '2019-02-01 16:54:55',TIMESTAMP '2019-02-01 17:32:47');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (2348,1134,'Поток - это минимальная единица выполнения программы, управляемая операционной системой.<br />Процесс - это группа связанных друг с другом потоков, существующих в едином ресурсном окружении.<br />Задача - единица работы, осуществляемая потоком.',true,'');
