INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (46,4,0,'При присваивании переменным того же типа, но меньшей размерности происходит имплицитный кастинг, если число позволяет, но при передаче в метод, ожидающий меньшей размерности, кастинг потребуется явный:<br />...<br />&nbsp;&nbsp;&nbsp; static void aaa(byte b) {}<br />&nbsp;&nbsp;&nbsp; static void bbb(float f) {}<br />&nbsp;&nbsp;&nbsp;&nbsp;<br />&nbsp;&nbsp;&nbsp; public static void main(String args[]) {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; aaa(1); //DOESNT COMPILE!<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; bbb(5.5); //DOESNT COMPILE!<br />&nbsp;&nbsp;&nbsp; }<br />...',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (47,4,0,'Если с целочисленным параметром вызывается метод, для которого есть перезагрузки с float и double - запустится метод с float:<br />...<br />&nbsp;&nbsp;&nbsp; static void ccc(double d) { System.out.println("ccc double");}<br />&nbsp;&nbsp;&nbsp; static void ccc(float f) { System.out.println("ccc float");}<br />&nbsp;&nbsp;&nbsp; <br />&nbsp;&nbsp;&nbsp; public static void main(String args[]) {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ccc(6);<br />&nbsp;&nbsp;&nbsp; }<br />...<br />&gt;&gt;&gt; ccc float',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (48,4,0,'Если byte передается в метод, у которого есть перезагрузки с int и short, запустится short:<br />...<br />&nbsp;&nbsp;&nbsp; static void ccc(int d) { System.out.println("ccc int");}<br />&nbsp;&nbsp;&nbsp; static void ccc(short d) { System.out.println("ccc short");}<br />&nbsp;&nbsp;&nbsp;&nbsp;<br />&nbsp;&nbsp;&nbsp; public static void main(String args[]) {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; byte b = 6;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ccc(b);<br />&nbsp;&nbsp;&nbsp; }<br />...<br />&gt;&gt;&gt; ccc short',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (49,4,0,'Возможно присваивать&nbsp;числовой литерал переменным меньшей размерности (short, byte) - при условии, что&nbsp;допустим имплицитный кастинг:<br />byte b = 13;<br />byte c = 133; //DOESN''T COMPILE<br /><br />Однако при передаче целочисленного литерала в метод, который принимает параметр меньшей размерности, требуется явный кастинг:<br /><br />&nbsp;&nbsp;&nbsp; static void ccc(short d) { }<br /><br />&nbsp;&nbsp;&nbsp; public static void main(String args[]) {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ccc(3);&nbsp;//DOESN''T COMPILE<br />&nbsp;&nbsp;&nbsp; }',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (50,4,0,'Литерал double нельзя присвоить переменной float.<br /><br />float b = 13.8; //DOESN''T COMPILE<br /><br />Также&nbsp;для передачи double-литерала в метод, который принимает параметр float, потребуется явный кастинг:<br /><br />&nbsp;&nbsp;&nbsp; static void ccc(float f) { }<br /><br />&nbsp;&nbsp;&nbsp; public static void main(String args[]) {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ccc(3.5);&nbsp;//DOESN''T COMPILE<br />&nbsp;&nbsp;&nbsp; }',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
