INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (111,4,2,'Перечислите унарные операторы Java.',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '2019-04-07 22:43:42');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (301,111,'<strong>+</strong> - означает, что число положильное, не оказывает влияния на значение выражения;<br />- - означает, что число отрицательное, меняет знак выражения;<br /><strong>++</strong> - инкремент;<br />-- - декремент;<br /><strong>!</strong> - инвертирует булево значение<br />~ - побитовое отрицание',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (112,4,2,'Что будет, если применить смену знака к <strong>char</strong>?',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '2019-03-28 23:16:12');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (4103,112,'получится <strong>int</strong>.<br /><br />Например:<br />char r = ''3'';<br />System.out.println(-r);<br />System.out.println(-''5'');',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (113,4,2,'Расскажите о сложении применительно к <strong>char</strong>.',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '2019-03-28 23:53:32');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (4105,113,'Если добавить число к переменной типа <strong>char</strong>, то она будет ссылаться на другой символ Unicode. Но такое добавление может произойти только посредством инкремента/декремента или короткого сложения/вычитания:<br /><br />char x=''A'';<br />x+=1;<br />x++;<br />x = x+1; //DOESN''T COMPILE<br /><br />При сложении двух <strong>char</strong> они преобразуются к <strong>int</strong>, равно как и их сумма.',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (114,4,2,'Чем отличаются пре-инкремент и пост-инкремент?',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (302,114,'Если использовать пре-инкремент в выражении, то значение операнда сначала изменяется, а потом подставляется в выражение. В случае пост-инкремента в выражение подставляется оригинальное значение переменной, и только потом происходит изменение.<br /><strong>Важно</strong>! Если в выражение имеется несколько инкрементов одной переменной, то изменение значения происходит при каждом использовании (до или сразу после подстановки, в зависимости от пре/пост).<br />Если инкремент идет как самостоятельное выражение, то разницы нет.<br />Аналогично для декремента.',true,'');
