INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (121,4,0,'В операторах сравнения (relational operators, &lt;,&lt;=,&gt;,&gt;=) идет преобразование типов аналогично бинарным арифметическим операциях',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (122,4,0,'Оператор <strong>instanceof</strong> тоже считается оператором сравнения (relational).',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (123,4,2,'Перечислите логические <span style="text-decoration: underline;">бинарные</span> операторы Java и типы операндов, к которым они применяются.',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (306,123,'Логическое "и": &amp;<br />Логическое "или" (включающее, inclusive): <strong>|</strong><br />Логическое "или" (исключающее, exclusive): <strong>^</strong><br /><br />Применяются к булевым операндам, а также к числовым (в этом случае называются <em>bitwise operators</em>, в OCA не рассматриваются)',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (124,4,0,'Одно из достоинств условных операторов короткого действия, помимо улучшения производительности, заключается в удобстве проверки на null:<br />if(x != null &amp;&amp; x.getValue() &lt; 5) {<br />&nbsp; // Do something<br />}<br /><br />Если бы в вышеприведенном примере кода использовался обычный оператор &amp;, то выдавалась бы Runtime-ошибка NullPointerException',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (125,4,2,'Каков вывод следующего фрагмента кода:<br /><br />int x = 6;<br />boolean y = (x &gt;= 6) || (++x &lt;= 7);<br />System.out.println(x);',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (307,125,'6.&nbsp;&nbsp;Вторая часть выражения не вычисляется, т.к. первая равна true',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (126,4,0,'Операторы проверки равенства (equality operators, !=, ==) ведут к преобразованию числовых операндов аналогично арифметическим бинарным операторам.',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (127,4,0,'В операторах проверки равенства нельзя смешивать разные типы операндов. Если сравниваются объекты, то они должны быть из одной иерархии наследования.<br /><br />boolean x = true == 3; // DOES NOT COMPILE<br />boolean y = false != "Giraffe"; // DOES NOT COMPILE<br />boolean z = 3 == "Kangaroo"; // DOES NOT COMPILE<br /><br />String s1 = "java";<br />StringBuilder s2 = new StringBuilder("java");<br />boolean s = s1 == s2; //DOES NOT COMPILE<br /><br /><br />Следует обращать пристальное внимание на типы данных, когда в экзаменационной задаче присутствует оператор проверки равенства.',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (128,4,1,'Какой результат получится в результате выполнения кода:<br /><br />boolean y = false;<br />boolean x = (y = true);<br />System.out.println(x);',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (308,128,'true',true,'во второй строке имеет место присваивание y (=), а не проверка равенства (==)');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (309,128,'false',false,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (129,4,2,'Когда сравнение объектов (==) возвращает true?',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (310,129,'Тогда и только тогда, когда обе ссылки указывают на один и тот же объект, либо обе ссылки равны null.',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (130,4,0,'Инструкция Java (<em>statement</em>) - часть кода, которая заканчивается точкой с запятой <strong>;</strong>',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (131,4,0,'На экзамене часто пытаются запутать экзаменуемого отступами. Например, в примере<br /><br />if(hourOfDay &lt; 11)<br />&nbsp; System.out.println("Good Morning");<br />&nbsp;&nbsp;morningGreetingCount++;<br /><br />может показаться, что инкремент выполнится только при выполнении условия, хотя на самом деле он выполняется всегда.',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (132,4,2,'Скомпилируется ли следующий код, и если нет, то почему?<br /><br />if(hourOfDay &lt; 15) {<br />&nbsp;System.out.println("Good Afternoon");<br />} else if(hourOfDay &lt; 11) {<br />&nbsp;System.out.println("Good Morning");<br />} else {<br />&nbsp;System.out.println("Good Evening");<br />}',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (311,132,'Скомпилируется. Несмотря на то, что условие во втором if никогда не выполнится (оно поглощено условием в первом if), компилятор не возьмет на себя смелость детектировать недостижимый код.',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (133,4,0,'Выражения в тернарном операторе должны возвращать значения:<br /><br />int hourOfDay = 9;<br />System.out.println(hourOfDay &lt; 15 ? 4 : "bbb");<br />System.out.println(hourOfDay &lt; 15 ?&nbsp;System.out.println() : "bbb"); //DOESN''T COMPILE',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (134,4,0,'<p>В тернарном операторе вычисляется только то выражение, которому передается выполнение исходя из условия:<br /><br />int y = 1;<br />int z = 1;<br />final int x = y&lt;10 ? y++ : z++;<br />System.out.println(y+","+z); // Outputs 2,1</p>
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (135,4,2,'Какие типы допустимы для переменной в конструкции switch?',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (312,135,'1) Все целочисленные, кроме long<br />2) Перечислимые<br />3) Строки<br />4) Целочисленные классы обертки, кроме Long',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (136,4,2,'Какие значения могут участвовать в <strong>case</strong>?',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '2019-04-07 22:54:31');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (313,136,'Только константы, рассчитываемые на этапе компиляции, причем того же типа, что и выражение в switch:<br />1) литералы<br />2) константы перечислимых типов<br />3) константы примитивных целочисленных типов (кроме long) с&nbsp;модификатором final. NB! Присваивание константы должно быть без автоанбоксинга!',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (137,4,0,'final-константа - это переменная с модификатором <strong>final</strong>, инициализированная литералом в том же выражении, где объявлена.',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (138,4,0,'В конструкции <strong>switch</strong> блок <strong>default</strong> может быть в любом месте (не обязательно в конце). Важно понимать, что выполнится он <span style="text-decoration: underline;">только</span> в том случае, если не подойдет ни одно значение из <strong>case</strong>. Однако если он не в конце и не имеет инструкции <strong>break</strong>, то выполнение после него перейдет следующему за ним <strong>case</strong>.',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (139,4,0,'В конструкциях while, do-while, for (включая for-each), как и в if, если тело цикла состоит из одного выражения, фигурные скобки можно опустить. Но этим единственным выражением не может быть объявление переменной, даже с присваиванием!',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '2019-03-21 21:31:05');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (140,4,2,'Опишите синтаксис цикла do-while',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (314,140,'do {<br />&nbsp; //body<br />} while (expression);',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (141,4,2,'Каково содержательное отличие циклов while и do-while?',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (315,141,'В цикле do-while тело цикла выполнится по крайней мере 1 раз.',true,'');
