INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (150,4,0,'Одним из недостатков цикла for-each является то, что он скрывает переменную-итератор, поэтому некоторые задачи&nbsp;решать с помощью таких циклов оказывается не так удобно. Примеры:<br /><br />1) разделение запятыми элементов коллекции&nbsp;(нельзя определить, где начало и конец перебора).<br />2) обращение во время одной итерации к нескольким элементам коллекции (нельзя обратиться по индексу)',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (151,4,2,'Что такое метка в Java?',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '2019-03-28 21:30:56');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (325,151,'Метка (<em>label</em>) - это опциональный указатель в начале Java-инструкции, который позволяет потоку приложения "перепрыгнуть" к определенному месту программы или прервать программу с определенного места. Правила именования такие же, как для переменных. Используются только при работе с вложенными циклами (хотя одинокая метка <span style="text-decoration: underline;">перед выражением</span> скомпилируется и без цикла). Общепринятая практика именования - заглавные буквы, разделенные подчеркиванием.',true,'');
