INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (153,4,2,'Чем <em>continue</em> отличается от <em>break</em>? А чем похоже?',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (327,153,'<em>break</em> прерывает цикл, на который ссылается (либо текущий, если метка не указана).<br /><em>continue</em> передает управление&nbsp;проверке условия выполнения цикла, на который ссылается (либо текущего цикла, если метка не указана). Фактически, <em>continue</em> прерывает текущую итерацию цикла.<br />Сходство состоит в том, что синтаксис использования <em>break</em> и <em>continue </em>одинаковый, так же как и логика выбора адресного цикла в зависимости от наличия/отсутствия метки.',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (154,4,2,'<p>Где могут быть использованы метки, <em>break</em>, <em>continue</em>?</p>',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '2019-04-07 23:19:28');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (328,154,'Метки допустимы перед любой Java-инструкцией, в том числе перед блоком&nbsp;(но использовать их не надо). Недопустимы перед классами, методами и объявлениями переменных.<br /><em>break</em> с меткой допустим внутри блока, если&nbsp;какой-то из окружающих блоков (либо управляющая конструкция, открывающая блок) помечен этой меткой. Правда, при этом&nbsp;не скомпилируется ситуация с unreachable code.<br /><em>break</em> без метки допустим внутри цикла или <em>switch</em>.<br /><em>continue</em> допустим внутри цикла.',true,'');
