	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (3466,184,'<strong><br /></strong>int&nbsp;<strong>indexOf</strong>(int <em>ch</em>)<br />int&nbsp;<strong>indexOf</strong>(int <em>ch</em>, int <em>fromIndex</em>)<br />int&nbsp;<strong>indexOf</strong>(String <em>str</em>)<br />int&nbsp;<strong>indexOf</strong>(String <em>str</em>, int <em>fromIndex</em>)<br /><br />возвращает индекс первого вхождения символа/строки, -1 в случае отсутствия<br /><br />На экзамене передаваться в этот метод будут только параметры char (которые имплицитно кастятся в int)',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (185,4,2,'Что будет напечано в результате выполнения следующего кода?<br /><br />String string = "animals";<br />System.out.println(string.indexOf(''a''));<br />System.out.println(string.indexOf("al"));<br />System.out.println(string.indexOf(''a'', 4));<br />System.out.println(string.indexOf("al", 5));',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
