	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (3467,186,'String&nbsp;<strong>substring</strong>(int <em>beginIndex</em>)<br /><br />возвращает подстроку. <em>beginIndex</em> включается.<br /><br /><em>IndexOutOfBoundsException</em> - если beginIndex отрицательный или &gt; length()<br /><br />String&nbsp;<strong>substring</strong>(int <em>beginIndex, int endIndex</em>)<br /><br />возвращает подстроку. <em>beginIndex</em> включается, <em>endIndex</em> не включается.<br /><br /><em>IndexOutOfBoundsException</em> - если beginIndex отрицательный, или <em>endIndex</em> &gt; length(), или <em>beginIndex &gt; </em><em>endIndex</em>',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (187,4,0,'Поскольку символ, соответствующий второму параметру в методе String.<strong>substring</strong>(beginIndex, endIndex), не включается в возвращаемое значение, значение этого параметра может превышать на единицу максимальный индекс строки (что тождественно опусканию этого параметра). Если же второй параметр превысит максимальный индекс строки более чем на единицу, то будет Runtime Exception:<br /><br />Exception in thread "main" java.lang.StringIndexOutOfBoundsException: String index out of range',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (188,4,2,'Какие ограничения накладываются на параметры в вызове метода String.substring(int, [int])?',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
