INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (251,4,2,'Объясните, что такое <em>autoboxing</em> и приведите примеры использования.',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '2019-03-21 22:37:59');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (488,251,'Autoboxing - автоматическое преобразование типов при работе с классами обертки.<br /><br />примеры использования:<br /><br />1) списки:<br /><br />методы <strong>add</strong>() и <strong>remove</strong>() преобразуют примитивы в объекты обертки:<br /><br />List&lt;Double&gt; weights = new ArrayList&lt;&gt;();<br />weights.add(50.5);<br /><br />метод <strong>get</strong>() возвращает примитив:<br /><br />double first = weights.get(0);<br /><br />2) Если ссылке типа Object присвоить примитив, то сработает autoboxing;<br /><br />//TODO. <em><strong>Найти еще примеры и добавить в вопрос!</strong></em>',true,'');
