INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (260,0,0,'Основной API для работы с java.util.<strong>Calendar</strong>.<br /><br />//текущие дата и время<br />Date d = new Date(); <br /><br />//создание заданной даты<br />Calendar c = Calendar.getInstance();<br />c.set(2015, Calendar.JANUARY, 1);<br />Date jan = c.getTime();<br /><br />или<br /><br />Calendar c = new GregorianCalendar(2015, Calendar.JANUARY, 1);<br />Date jan = c.getTime();<br /><br />или<br /><br />Calendar c = Calendar.getInstance();<br />c.set(2015, 0, 1); //январь<br />Date jan = c.getTime();<br /><br />//сдвиг на один день<br /><br />public Date subtractDay(Date date) {<br />&nbsp;&nbsp;&nbsp; Calendar cal = Calendar.getInstance();<br />&nbsp;&nbsp;&nbsp; cal.setTime(date);<br />&nbsp;&nbsp;&nbsp; cal.add(Calendar.DATE, -1);<br />&nbsp;&nbsp;&nbsp; return cal.getTime();<br />}',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
