INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (275,4,2,'Дайте общую информацию о классе java.time.format.<strong>DateTimeFormatter</strong>',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '2019-03-23 18:33:33');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (3485,275,'<br />Класс, осуществляющий форматирование даты/времени.<br /><br />Имеет несколько констант с экземплярами, например<br /><br /><strong>ISO_LOCAL_DATE</strong> (''2011-12-13'')<br /><strong>ISO_LOCAL_TIME </strong>(''10:15'' или ''10:15:30'')<strong><br />ISO_LOCAL_DATE_TIME</strong> (''2011-12-13T10:15:30'')<br /><br />используется в форматировании следующим образом:<br /><br />LocalDate date = LocalDate.of(2020, Month.JANUARY, 20);<br />LocalTime time = LocalTime.of(11, 12, 34);<br />LocalDateTime dateTime = LocalDateTime.of(date, time);<br />System.out.println(date.format(DateTimeFormatter.ISO_LOCAL_DATE));<br />System.out.println(time.format(DateTimeFormatter.ISO_LOCAL_TIME));<br />System.out.println(dateTime.format(DateTimeFormatter.ISO_LOCAL_DATE_TIME));',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (276,4,2,'Опишите работу класса java.time.format.<strong>DateTimeFormatter</strong> с локалями',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '2019-03-23 18:37:58');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (3486,276,'<br />static <strong>ofLocalizedDate</strong>(FormatStyle s<em>tyle</em>)<br />static <strong>ofLocalizedTime</strong>(FormatStyle s<em>tyle</em>)<br />static <strong>ofLocalizedDateTime</strong>(FormatStyle s<em>tyle</em>)<br /><br />FormatStyle - это enum.<br /><br />Методы возвращают форматтеры, специфичные для локали.<br /><br />если локаль надо задать жестко, то можно вызвать метод <strong>withLocale</strong>(Locale) на результате этих методов.<br /><br />Примеры:<br /><br />DateTimeFormatter shortDate =<br />&nbsp;&nbsp;&nbsp; DateTimeFormatter.ofLocalizedDate(FormatStyle.SHORT);<br />System.out.println(shortDate.format(dateTime)); // 1/20/20<br />System.out.println(shortDate.format(date)); // 1/20/20<br />System.out.println(shortDate.format(time)); // UnsupportedTemporalTypeException',true,'');
