INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (367,4,2,'Каким будет вывод следующего кода?<br /><br />public class InitializationOrderSimple {<br />&nbsp;&nbsp;&nbsp; private String name = "Torchie";<br />&nbsp;&nbsp;&nbsp; { System.out.println(name); }<br />&nbsp;&nbsp;&nbsp; private static int COUNT = 0;<br />&nbsp;&nbsp;&nbsp; static { System.out.println(COUNT); }<br />&nbsp;&nbsp;&nbsp; static { COUNT += 10; System.out.println(COUNT); }<br />&nbsp;&nbsp;&nbsp; public InitializationOrderSimple() {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; System.out.println("constructor");<br />&nbsp;&nbsp;&nbsp; } <br />}<br />...<br />public class CallInitializationOrderSimple {<br />&nbsp;&nbsp;&nbsp; public static void main(String[] args) {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; InitializationOrderSimple init = new InitializationOrderSimple();<br />&nbsp;&nbsp;&nbsp; } <br />}',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
