INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (368,4,2,'Каким будет вывод следующего кода?<br /><br />public class InitializationOrder {<br />&nbsp;&nbsp;&nbsp; private String name = "Torchie";<br />&nbsp;&nbsp;&nbsp; { System.out.println(name); }<br />&nbsp;&nbsp;&nbsp; private static int COUNT = 0;<br />&nbsp;&nbsp;&nbsp; static { System.out.println(COUNT); }<br />&nbsp;&nbsp;&nbsp; { COUNT++; System.out.println(COUNT); }<br />&nbsp;&nbsp;&nbsp; public InitializationOrder() {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; System.out.println("constructor");<br />&nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp; public static void main(String[] args) {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; System.out.println("read to construct");<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; new InitializationOrder();<br />&nbsp;&nbsp;&nbsp; }<br />}',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
