INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (424,4,0,'Опасная ситуация на экзамене: если у родительского класса нет дефолтного конструктора, а дочерний класс объявлен без конструкторов (т.е. с дефолтным), будет ошибка компиляции.',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
