INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (555,8,0,'Одна из функций <strong>instanceof</strong> - предварять кастинг объектов.<br /><br />public void feedAnimal(Animal animal) {<br />&nbsp;&nbsp;&nbsp; if(animal instanceof Cow) {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ((Cow)animal).addHay();<br />&nbsp;&nbsp;&nbsp; } else if(animal instanceof Bird) {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ((Bird)animal).addSeed();<br />&nbsp;&nbsp;&nbsp; } else if(animal instanceof Lion) {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ((Lion)animal).addMeat();<br />&nbsp;&nbsp;&nbsp; } else {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; throw new RuntimeException("Unsupported animal");<br />&nbsp;&nbsp;&nbsp; } <br />}',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
