	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (1285,556,'Это определение в суперклассе метода, имплементация которого предполагается в дочерних классах. Это позволяет вызывать метод на ссылке типа суперкласса (например, при передаче такой ссылки в сервисный метод).<br /><br />В большинстве случаев вышеуказанный метод будет абстрактным.',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (557,8,2,'Что будет напечатано после выполнения следующего кода?<br /><br />abstract class Animal {<br />&nbsp;&nbsp;&nbsp; String name = "???";<br />&nbsp;&nbsp;&nbsp; public void printName() {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; System.out.println(name);<br />&nbsp;&nbsp;&nbsp; }<br />}<br />class Lion extends Animal {<br />&nbsp;&nbsp;&nbsp; String name = "Leo";<br />}<br />public class PlayWithAnimal {<br />&nbsp;&nbsp;&nbsp; public static void main(String... args) {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Animal animal = new Lion();<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; animal.printName();<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Lion lion = new Lion();<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; lion.printName();<br />&nbsp;&nbsp;&nbsp; }<br />}',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '2019-04-17 13:28:02');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (1286,557,'???<br />???<br /><br />По результату animal.<strong>printName</strong>() может показаться, что сработало правило доступа к полям (на ссылке доступны поля экземпляра типа ссылки, инкапсулированного в экземпляр реального класса-потомка). Но пример с lion.<strong>printName</strong>() показывает, что унаследованный и непереопределенный метод берет переменную с инкапсулированного экземпляра того класса, в котором определен.',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (558,8,2,'Что будет напечатано в результате выполнения следующего кода?<br /><br />abstract class Animal {<br />&nbsp;&nbsp;&nbsp; public void careFor() {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; play();<br />&nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp; public void play() {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; System.out.println("pet animal");<br />&nbsp;&nbsp;&nbsp; } <br />}<br />class Lion extends Animal {<br />&nbsp;&nbsp;&nbsp; public void play() {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; System.out.println("toss in meat");<br />&nbsp;&nbsp;&nbsp; } <br />}<br />public class PlayWithAnimal {<br />&nbsp;&nbsp;&nbsp; public static void main(String... args) {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Animal animal = new Lion();<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; animal.careFor();<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Lion lion = new Lion();<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; lion.careFor();<br />&nbsp;&nbsp;&nbsp; } <br />}',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '2019-04-17 13:31:29');
