INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (635,8,0,'Абстрактные определения методов можно дублировать в наследующем/реализующем&nbsp; абстрактном классе/интерфейсе - это не ошибка компиляции.',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (636,8,0,'Функциональный интерфейс можно снабдить аннотацией <strong>@FunctionalInterface</strong>, тогда будет ошибка компиляции, если интерфейс неудовлетворяет условиям функционального (например, содержит более одного метода). Для использования корректного функционального интерфейса в лямбда-выражениях наличие вышеуказанной аннотации необязательно. Однако лучше ее все-таки указывать, чтобы другой разработчик случайно не испортил функциональный интерфейс.',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
