INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (652,8,2,'Назовите правила кастинга объектов.',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (1478,652,'<strong>1</strong>.&nbsp;Преобразование от дочернего типа к родительскому не требует явного кастинга.<br /><strong>2</strong>.&nbsp;Преобразование от родительского&nbsp; типа к дочернему требует явного кастинга.<br /><strong>3</strong>. Компилятор не позволяет кастинг несвязанных одной иерархией типов.<br /><strong>4</strong>. Отсутствие проблем на этапе компиляции не означает, что кастинг осуществится при запуске программы.',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (653,8,0,'<p>Технически можно обеспечить любой кастинг без ошибок компиляции - достаточно ввести промежуточный кастинг к любому общему предку:<br /><br />class Bird{}<br />class Fish{}<br />...<br />Bird bird = new Bird();<br />Fish fish = (Fish)(Object)bird;</p>',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (654,8,0,'Компилируемость конструкций с кастингом&nbsp;тесно связано с таковой для конструкций с <strong>instanceof</strong>. Например, можно делать кастинг класса к любому интерфейсу и наоборот, если класс&nbsp;не финальный. Также принято кастинг предварять проверкой <strong>instanceof</strong>, чтобы исключить <strong>ClassCastException</strong>.',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
