INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (656,8,2,'Что такое <em>инвариант</em> применительно к конструированию классов?',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (1480,656,'Инвариант - это свойство/набор свойств/набор фактов, которые мы вправе ожидать от некоторого класса. Например, мы можем ыть уверены, что некоторое поле класса содержит целочисленное значение, большее нуля - это инвариант.<br />Целостность инвариантов обеспечивается, в первую очередь, геттерами, сеттерами и конструкторами.',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (657,8,0,'Если установка некоторого свойства класса происходит согласно каким-то четким условиям, из конструктора следует вызывать сеттер с необходимой логикой.<br /><br />public class Animal {<br />&nbsp;&nbsp;&nbsp; private String species;<br /><br />&nbsp;&nbsp;&nbsp; public Animal(String species) {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; this.setSpecies(species);<br />&nbsp;&nbsp;&nbsp; }<br /><br />&nbsp;&nbsp;&nbsp; public String getSpecies() {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return species;<br />&nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp; <br />&nbsp;&nbsp;&nbsp; public void setSpecies(String species) {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if(species == null || species.trim().length()==0) {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; throw new IllegalArgumentException("Species is required");<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; this.species = species;<br />&nbsp;&nbsp;&nbsp; }<br />}',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
