INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (731,8,2,'Чем использование неограниченного <em>wildcard</em> (List&lt;?&gt;) лучше <em>raw type</em> (List)?',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
