	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (1648,739,'&lt;T&gt; &lt;? extends T&gt; method2(List&lt;? extends T&gt; list) {<br />&nbsp;&nbsp;&nbsp; return list.get(0);<br />}',false,'Возвращаемый тип не может содержать <em>wildcard</em>');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (1649,739,'&lt;B extends A&gt; B method3(List&lt;B&gt; list) {<br />&nbsp;&nbsp;&nbsp; return new B();<br />}',false,'Обозначение B использовано как дженерик и поэтому внутри метода теряет свою интерпретацию как имя реального класса. А создать объекты типа дженерика - нельзя.');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (1650,739,'void method4(List&lt;? super B&gt; list) {<br />}',true,'');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (1651,739,'&lt;X&gt; void method5(List&lt;X super B&gt; list) {<br />}',false,'Нельзя использовать формальный тип параметра в <em>wildcard</em>-контексте. Должно быть <strong>List</strong>&lt;? super <strong>B</strong>&gt;');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (740,8,0,'<em>Bounds</em> можно использовать не только с <em>wildcards</em>, но и как ограничение на использование дженерика в классе или методе:<br /><br />class Generic&lt;T extends Number&gt; {<br />&nbsp;&nbsp;&nbsp; T getT(T t) {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return t;<br />&nbsp;&nbsp;&nbsp; }<br />}<br /><em>&nbsp; или</em><br />static &lt;T extends Number&gt; void test(T t) {}',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (741,8,2,'Перечислите основные интерфейсы <em>Java Collections Framework</em>.',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (1652,741,'1. <strong>List</strong>: упорядоченный набор элементов с повторениями; элементы доступны по индексу.<br />2. <strong>Set</strong>: набор элементов без повторений.<br />3. <strong>Queue</strong>: набор элементов с особым порядком обработки, обычно FIFO<br />4. <strong>Map</strong>: набор пар "ключ-значение"; значения доступны по ключу, повторения ключей запрещены.',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (742,8,0,'Интерфейсы <strong>List</strong>, <strong>Set</strong>, <strong>Queue</strong> являются наследниками <strong>Collection</strong>. <strong>Map</strong> - не является.',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (743,8,2,'Перечислите общие методы для всех коллекций.',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (1653,743,'<strong>1</strong><em>. boolean </em><strong>add</strong>(<strong>E</strong> <em>element</em>) // E - формальный тип параметра<br /><br />(возвращает <strong>true</strong>, если удалось добавить, т.е. коллекция изменилась)<br /><br /><strong>2</strong>. <em>boolean</em> <strong>remove</strong>(<strong>Object </strong><em>object</em>) - удаление конкретного объекта<br /><em>boolean</em> <strong>remove</strong>(<strong>int</strong> <em>index</em>) - удаление по индексу, IndexOutOfBoundsException в случае неверного индекса<br /><br />(возвращает <strong>true</strong>, если удалось удалить, т.е. коллекция изменилась)<br /><br /><strong>3</strong>. <em>boolean </em><strong>isEmpty</strong>()<br /><strong><br />4</strong>. <em>int </em><strong>size</strong>()<br /><strong><br />5</strong>. void <strong>clear</strong>()<br /><strong><br />6</strong>. <em>boolean </em><strong>contains</strong>(<strong>Object</strong> <em>object</em>)<br /><br />(сравнение производится по <strong>equals</strong>)',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (744,8,0,'Если в коллекции есть несколько одинаковых элементов, то <strong>remove</strong>(Object) удалит только один из них',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (745,8,2,'Назовите основные преимущества <strong>ArrayList</strong>.',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (1654,745,'1. Доступ к любому элементу за фиксированное время.<br />2. Добавление/удаление занимает больше времени, чем чтение<br /><br />Резюме: следует использовать, если чтение из списка производится чаще, чем запись в него.',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (746,8,2,'Перечислите основные времена оценки быстродействия.',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (1655,746,'1. O(1) - постоянное время; время операции не зависит от объема данных. Пример: чтение из ArrayList<br /><br />2. O(log n) - логарифмическое время; время операции возрастает значительно медленнее, чем объем данных. Пример - двоичный поиск.<br /><br />3. O(n) - линейное время; время операции увеличивается линейно по мере роста объема данных. Пример: выборка из списка всех элементов, равных данному.<br /><br />4. O(n^2) - n-квадратное время; время операции увеличивается квадратично по мере роста объема данных. Пример - вложенная итерация по одной и той же коллекции.',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (747,8,2,'Назовите особенности и преимущества <strong>LinkedList</strong>',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (1656,747,'1. Реализует интерфейсы <strong>List</strong> и <strong>Queue<br /></strong>2. Доступ, добавление и удаление для <span style="text-decoration: underline;">граничных</span> элементов занимает постоянное время.<br />3. Аналогичные операции применительно к произвольному индексу занимают линейное время.<br /><br />Резюме: следует использовать в качестве очереди.',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (748,8,0,'<strong>Vector</strong> работал медленно, зато был Thread-safe',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (749,8,2,'Перечислите основные методы <strong>List</strong> (за исключением унаследованных из <strong>Collection</strong>)',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (1657,749,'1. <em>void</em> <strong>add</strong>(<strong>int</strong> <em>index</em>, <strong>E</strong> <em>element</em>)<br /><br />вставляет элемент на указанное место со сдвигом оставшейся части вперед.<br /><br />2. <em>E</em> <strong>get</strong>(<strong>int</strong> <em>index</em>)<br /><br />возвращает элемент по индексу<br /><br />3. <em>int </em><strong>indexOf</strong>(<strong>Object</strong> <em>o</em>)<br /><br />возвращает первый встретившийся индекс указанного элемента либо -1<br /><br />4. <em>int </em><strong>lastIndexOf</strong>(<strong>Object</strong> <em>o</em>)<br /><br />возвращает последний встретившийся индекс указанного элемента либо -1<br /><br />5. <em>void</em> <strong>remove</strong>(<strong>int </strong><em>index</em>)<br /><br />удаляет элемент по индексу и сдвигает оставшуюся часть влево.<br /><br />6. <strong>E</strong> <em>set</em>(<strong>int</strong> <em>index</em>, <strong>E</strong> <em>e)<br /><br /></em>заменяет элемент по индексу и возвращает предыдущий',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (750,8,0,'<strong>Vector</strong> и <strong>Stack</strong> тоже реализуют интерфейс <strong>List</strong>.',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (751,8,2,'Назовите 3 способа проитерироваться по списку.',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (1658,751,'1. Используя for-each:<br /><br />for (String string: list) {<br />&nbsp;&nbsp;&nbsp; System.out.println(string);<br />}<br /><br />2. Используя обычный for:<br /><br />for (int i = 0; i&lt;list.size(); i++) {<br />&nbsp;&nbsp;&nbsp; System.out.println(list[i]);<br />}<br /><br />3. Используя итератор:<br /><br />Iterator&lt;String&gt; iter = list.iterator();<br />while(iter.hasNext()) {<br />&nbsp;&nbsp;&nbsp; String string = iter.next();<br />&nbsp;&nbsp;&nbsp; System.out.println(string);<br />}',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (752,8,2,'Опишите особенности и преимущества <strong>HashSet</strong>.',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (1659,752,'HashSet использует хеш-таблицу для хранения элементов на основе метода <strong>hashCode</strong>(). Операция добавления, как операция проверки вхождения, осуществляется за фиксированное время. Порядок не сохраняется.',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (753,8,2,'Опишите особенности и преимущества <strong>TreeSet</strong>.',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (1660,753,'Набор всегда отсортирован. Операции добавления и проверки вхождения занимают O(log n).',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (754,8,0,'<strong>TreeSet</strong> реализует интерфейс <strong>NavigableSet</strong>.',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (755,8,0,'В <strong>TreeSet</strong> можно поместить только объект, реализующий интерфейс <strong>Comparable</strong>, иначе будет ClassCastException.',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (756,8,0,'<strong>Set</strong> не имеет каких-то специфичных методов, которых не было бы в <strong>Collection</strong>. (<em>на уровне экзамена</em>)',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (757,8,2,'Перечислите специфичные методы интерфейса <strong>NavigableSet</strong>.',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (1661,757,'<em>E </em><strong>lower</strong>(E <em>e</em>) - возвращает наибольший элемент, строго меньший <em>e</em> (или <strong>null</strong> если такого элемента нет)<br /><em>E</em> <strong>floor</strong>(E <em>e</em>) - возвращает наибольший элемент, меньший либо равный <em>e</em> (или <strong>null</strong> если такого элемента нет)<br /><em>E</em> <strong>ceiling</strong>(E <em>e</em>)&nbsp; - возвращает наименьший элемент, больший либо равный <em>e</em> (<strong>или</strong> null если такого элемента нет)<br /><em>E</em> <strong>higher</strong>(E <em>e</em>) - возвращает наименьший элемент, строго больший <em>e</em> (<strong>или</strong> null если такого элемента нет)',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (758,8,0,'<strong>LinkedList</strong> реализует интерфейсы <strong>Queue</strong> и <strong>List</strong> (поэтому это не самый эффективный класс). Кроме того, <strong>LinkedList</strong> реализует двустороннюю (<em>double-ended</em>) очередь (<strong>Deque</strong>). Эталонный образец двусторонней очереди <strong>- ArrayDeque</strong>.<br /><br />Двусторонняя очередь, по сути, объединяет функциональность обычной очереди (интерфейса <strong>Queue</strong>) и стека (<span style="text-decoration: underline;">класса</span> <strong>Stack). </strong>Интерфейс <strong>Deque</strong> наследует интерфейсу <strong>Queue</strong>.',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (759,8,2,'Назовите основные методы интерфейса <strong>Queue</strong>&lt;E&gt;.',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '2019-04-25 13:01:18');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (1662,759,'<em>boolean</em> <strong>add</strong>(<strong>E</strong> <em>e</em>) - Добавляет элемент в конец очереди и возвращает <strong>true</strong> или выдает исключение (если имеем дело с очередью фиксированного размера)<br /><br /><em>E</em> <strong>element</strong>() - возвращает следующий элемент или выдает исключение, если очередь пуста<br /><br /><em>boolean </em><strong>offer</strong>(<strong>E</strong> <em>e</em>) - добавляет элемент в конец очереди, если очередь это позволяет, и возвращает успешность операции<br /><br /><em>E</em> <strong>remove</strong>()&nbsp; - удаляет и возвращает следующий элемент или выдает исключение, если очередь пуста<br /><br /><em>E</em> <strong>poll</strong>() - Возвращает и удаляет следующий элемент или возвращает <strong>null</strong>, если очередь пуста.<br /><br /><em>E</em> <strong>peek</strong>() - Возвращает следующий элемент или <strong>null</strong>, если очередь пуста<br /><br />',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (760,8,2,'Назовите основные методы структуры Stack (реализуемой с помощью интерфейса Deque).',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (1663,760,'<em>void</em> <strong>push</strong>(<strong>E</strong> <em>e</em>) - добавляет элемент в начало стека, выдавая исключение, если добавление невозможно.<br /><br /><a><em>E </em><strong><strong>peek</strong></strong>() - Возвращает следующий элемент или <strong><strong>null</strong></strong>, если стек пуст<strong><br /></strong><br /><em>E</em> <strong>pop</strong>() - удаляет и возвращает следующий элемент или выдает исключение, если стек пуст.<strong><br /><br /></strong></a>',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (761,8,2,'Назовите 3 наиболее популярных метода очередей (FIFO) и стеков (LIFO).',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (1664,761,'FIFO: offer/poll/peek, все они не выдают исключений<br />LIFO: push/poll/peek, push в Deque может выдать исключение',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (762,8,0,'Особенность двусторонней очереди - наличие методов <strong>push</strong> и <strong>pop</strong>',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (763,8,2,'Опишите API интерфейса <strong>NavigableMap</strong> (реализуемого классом <strong>TreeMap</strong>&lt;K,V&gt;), аналогичное <strong>NavigableSet</strong>',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '2019-04-25 17:47:37');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (6389,763,'Методы выглядят так:<br />K <strong>floorKey</strong>(<strong>K</strong> <em>key</em>)<br /><em>Map.Entry</em>&lt;<em>K</em>,<em>V</em>&gt; <strong>floorEntry</strong>(<strong>K</strong> <em>key</em>)<br /><br />и аналогично для <strong>ceiling</strong>, <strong>lower</strong>, <strong>higher</strong>. Все сравнения и сортировки берутся по ключам.',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (764,8,2,'Назовите особенности и преимущества <strong>HashMap</strong>',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (1665,764,'- постоянное время для извлечения и добавления элементов по ключу.<br />- порядок вставки не сохраняется',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (765,8,2,'Назовите особенности и преимущества <strong>TreeMap</strong>',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (1666,765,'- ключи хранятся в сортированном виде<br />- добавление и удаление занимает логарифмическое время.',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (766,8,2,'Назовите основные методы интерфейса <strong>Map</strong>&lt;<em>K</em>,<em>V</em>&gt;',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (1667,766,'<em>void</em> <strong>clear</strong>()<br /><em><br />boolean</em> <strong>isEmpty</strong>()<br /><em><br />int</em> <strong>size</strong>()<br /><em><br />V</em> <strong>get</strong>(<strong>Object</strong> <em>key</em>) Возвращает значение по ключу или null<br /><em><br />V</em> <strong>put</strong>(<strong>K</strong> <em>key</em>, <strong>V</strong> <em>value</em>) Добавляет пару ключ/значение. Возвращает предыдущее значение или null<br /><em><br />V</em> <strong>remove</strong>(<strong>Object</strong> <em>key</em>) Удаляет и возвращает значение по ключу либо null<br /><em><br />boolean</em> <strong>containsKey</strong>(<strong>Object</strong> <em>key</em>)<br /><em><br />boolean</em> <strong>containsValue</strong>(<strong>Object</strong> <em>value</em>)<br /><em><br />Set</em>&lt;<em>K</em>&gt; <strong>keySet</strong>() сет всех ключей<br /><em><br />Collection</em>&lt;<em>V</em>&gt; <strong>values</strong>() коллекция всех значений.',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (767,8,2,'Что напечатает следующий код?<br /><br />Map&lt;String, String&gt; map = new TreeMap&lt;&gt;();<br />map.put("koala", "bamboo");<br />map.put("lion", "meat");<br />map.put("giraffe", "leaf");<br />System.out.println(map.contains("lion"));',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (1668,767,'Будет ошибка компиляции. Интерфейс Map не содержит&nbsp; метода <strong>contains</strong>, только <strong>containsKey</strong> и <strong>containsValue</strong>.',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (768,8,0,'Устаревший класс <strong>Stack</strong>&lt;E&gt; реализует интерфейс <strong>List</strong>. Ни <strong>Queue</strong>, ни <strong>Deque</strong><br />',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (769,8,0,'В <strong>TreeSet</strong> и <strong>TreeMap</strong> нельзя добавлять <strong>null</strong> (будет runtime error).<br /><br />В <strong>ArrayDeque</strong> нельзя добавлять <strong>null</strong>, так как это служебное значение для очередей. (будет runtime error).<br /><br /><br />',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
