INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (798,8,2,'Опишите метод <strong>removeIf</strong> в интерфейсе <strong>Collection</strong>.',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '2019-04-26 09:48:15');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (6393,798,'Новый дефолтный метод Java 8, позволяющий удалять элементы из коллекции по условию.<br /><br /><em>boolean</em> <strong>removeIf</strong>(<em>Predicate&lt;? super E&gt;</em> filter)',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (799,8,2,'С помощью метода <strong>removeIf</strong> удалите из коллекции строк все элементы, начинающиеся с "A"',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (1685,799,'collection.removeIf(s -&gt; s.startsWith("A"));<br /><br />лямбда понимает, что нужен объект&nbsp; <strong>Predicate</strong>&lt;<em>String</em>&gt;, из сигнатуры <strong>removeIf<br /><br /></strong>Заменить лямбду на метод-ссылку не выйдет, т.к. участвует аргумент "А", который не удастся передать в метод: вызов по конкретному экземпляру невозможен, т.к. его нет, а вызов по неизвестному экземпляру позволяет только вызвать безаргументный метод на самом экземпляре.',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (800,8,2,'Опишите метод <strong>replaceAll</strong> в интерфейсе <strong>List</strong>',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '2019-04-26 09:43:54');
