	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (6396,804,'Новый дефолтный метод в Java 8.<br /><br /><em>V</em> <strong>putIfAbsent</strong>(<strong>K</strong> <em>key</em>, <strong>V</strong> <em>value</em>)<br /><br />Если ключа нет или значение <strong>null</strong>, то положит новое и вернет <strong>null</strong>, иначе вернет текущее.',true,'');
