INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (839,8,2,'<a>Проведите примеры лямбда-выражений, которые можно составить в методе everyonePlay c использованием интерфейса <strong>Gorilla</strong>.<br /><br />1: interface Gorilla { String move(); }<br />2: class GorillaFamily {<br />3:&nbsp;&nbsp;&nbsp;&nbsp; String walk = "walk";<br />4:&nbsp;&nbsp;&nbsp;&nbsp; void everyonePlay(boolean baby) {<br />5:&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; String approach = "amble", approach2 = "tra";<br />6:&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; approach2 = "run";<br />7:<br />8:&nbsp;&nbsp;&nbsp; }<br />9:&nbsp;&nbsp;&nbsp; void play(Gorilla g) {<br />10:&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; System.out.println(g.move());<br />11:&nbsp;&nbsp; }<br />12: } </a>',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
