INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (884,8,2,'Опишите промежуточные операции <strong>limit</strong>() и <strong>skip</strong>()',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (1871,884,'Операции уменьшают исходный поток. Конечный поток могут сделать меньше, бесконечный - сделать конечным.<br /><br /><em>Stream</em>&lt;<em>T</em>&gt; <strong>limit</strong>(<strong>long </strong><em>maxSize</em>)<br /><em>Stream</em>&lt;<em>T</em>&gt; <strong>skip</strong>(<strong>long </strong><em>n</em>)<br /><br /><strong>limit</strong> ограничивает поток заданным <em>maxSize</em> (отсекает все последующие элементы. <strong>skip</strong> игнорирует первые <em>n</em> элементов потока.<br /><br />Stream&lt;Integer&gt; s = Stream.iterate(1, n -&gt; n + 1);<br />s.skip(5).limit(2).forEach(System.out::print); // 67',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (885,8,2,'Опишите промежуточную операцию <strong>map</strong>().<br /><br />Приведите пример маппинга потока строк на их длины.',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (1872,885,'Операция осуществляет маппинг всех элементов потока в соответствии с некоторой функцией.<br /><br />&lt;<em>R</em>&gt; <em>Stream</em>&lt;<em>R</em>&gt; <strong>map</strong>(<strong>Function</strong>&lt;? super <em>T</em>, ? extends <em>R</em>&gt; <em>mapper</em>)<br /><br />Пример:<br /><br />Stream&lt;String&gt; s = Stream.of("monkey", "gorilla", "bonobo");<br />Stream&lt;Integer&gt; i = s.map(String::length);',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (886,8,2,'Опишите промежуточную операцию <strong>flatMap</strong>()',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (1873,886,'Операция берет содержимое каждого элемента и помещает вложенные элементы в новый поток, делая плоскую структуру.<br /><br />&lt;<em>R</em>&gt; <em>Stream</em>&lt;<em>R</em>&gt; <strong>flatMap</strong>(<strong>Function</strong>&lt;? super <em>T</em>, ? extends <em>Stream</em>&lt;? extends <em>R</em>&gt;&gt; <em>mapper</em>)<br /><br />Пример:<br />List&lt;String&gt; zero = Arrays.asList();<br />List&lt;String&gt; one = Arrays.asList("Bonobo");<br />List&lt;String&gt; two = Arrays.asList("Mama Gorilla", "Baby Gorilla");<br />Stream&lt;List&lt;String&gt;&gt; animals = Stream.of(zero, one, two);<br />Stream&lt;String&gt; flatMap = animals.flatMap(l -&gt; l.stream());<br /><br />метод не рекурсивный, чтобы "распаковать" Stream&lt;List&lt;List&lt;String&gt;&gt;&gt;, придется делать что-то вроде<br />Stream&lt;String&gt; flatMap = animals.flatMap(l -&gt; l.stream()).flatMap(l -&gt; l.stream());',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (887,8,2,'Опишите промежуточную операцию <strong>sorted</strong>()',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (1874,887,'Операция возвращает сортированный поток. Использует естественный порядок, если нет компаратора.<br /><br /><em>Stream</em>&lt;<em>T</em>&gt; <strong>sorted</strong>()<br /><em>Stream</em>&lt;<em>T</em>&gt; <strong>sorted</strong>(<strong>Comparator</strong>&lt;? super <em>T</em>&gt; <em>comparator</em>)<br /><br />',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (888,8,2,'Опишите промежуточную операцию <strong>peek</strong>()',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (1875,888,'Операция, предназначенная для отладочных целей. Не меняет поток. Позволяет выполнить некоторую операцию на переборе потока. !Не защищает от несанкционированной попытки изменить элемент потока.<br /><br /><em>Stream</em>&lt;<em>T</em>&gt; <strong>peek</strong>(<strong>Consumer</strong>&lt;? super <em>T</em>&gt; <em>action</em>)<br /><br />Пример: вывод содержимого потока в процессе обработки:<br /><br />Stream&lt;String&gt; stream = Stream.of("black bear", "brown bear", "grizzly");<br />long count = stream.filter(s -&gt; s.startsWith("g")).peek(System.out::println).count();<br /><br /><br />',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (889,8,2,'Дан список<br /><br />List&lt;String&gt; list = Arrays.asList("Toby", "Anna", "Leroy", "Alex");<br /><br />Используя потоки, выведите на печать ровно два элемента длиной в 4 символа, являющихся первыми в алфавитном порядке из всех 4-символьных (т.е. Alex, Anna).',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (1876,889,'list.stream().filter(n -&gt; n.length() == 4).sorted()<br />.limit(2).forEach(System.out::println);',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (890,8,2,'Что напечатается в результате работы следующего кода?<br /><br />Stream.generate(() -&gt; "Elsa")<br />&nbsp; .filter(n -&gt; n.length() == 4)<br />&nbsp; .sorted()<br />&nbsp; .limit(2)<br />&nbsp; .forEach(System.out::println);',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (1877,890,'Конвейер зависнет. Методу <strong>sorted</strong>() для правильной сортировки нужно получить на вход все элементы потока, а поток бесконечный. Метод <strong>filter</strong>() не зависнет, поскольку он может сразу передать элемент дальше по ленте конвейера, не дожидаясь, пока получит все.',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (891,8,2,'<a>Что напечатается в результате работы следующего кода?<br /><br />Stream.generate(() -&gt; "Elsa")<br />&nbsp; .filter(n -&gt; n.length() == 4)<br />&nbsp; .limit(2)<br />&nbsp; .sorted()<br />&nbsp; .forEach(System.out::println);<br /></a>',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (1878,891,'Elsa<br />Elsa<br /><br /><strong>filter</strong>() передает элементы дальше в <strong>limit</strong>() сразу, ему не нужны все элементы потока, <strong>limit</strong>() передаст первые два элемента в <strong>sorted</strong>(), после чего обрежет поток , а <strong>sorted</strong>() спокойно отсортирует конечный набор.',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (892,8,2,'<a>Что напечатается в результате работы следующего кода?<br /><br />Stream.generate(() -&gt; "Olaf Lazisson")<br />&nbsp; .filter(n -&gt; n.length() == 4)<br />&nbsp; .limit(2)<br />&nbsp; .sorted()<br />&nbsp; .forEach(System.out::println);<br /></a>',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (1879,892,'Поток зависнет в <strong>filter</strong>(), поскольку ни один его элемент никогда не будет передан в <strong>limit</strong>().',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (893,8,2,'Что напечатает следующий код?<br /><br />Stream&lt;Integer&gt; infinite = Stream.iterate(1, x -&gt; x + 1);<br />infinite.limit(5)<br />&nbsp; .peek(System.out::print)<br />&nbsp; .filter(x -&gt; x % 2 == 1)<br />&nbsp; .forEach(System.out::print);',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
