INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (895,8,2,'Перечислите способы распечатать содержимое потока с объяснением их особенностей.',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (1882,895,'1) s.forEach(System.out::println);<br /><br />завершает поток, зависает на бесконечных потоках<br /><br />2) System.out.println(s.collect(Collectors.toList()));<br /><br />завершает поток, зависает на бесконечных потоках<br /><br />3) s.peek(System.out::println).count();<br /><br />не завершает поток, зависает на бесконечных потоках (<strong>count</strong> нужен как терминальная операция, иначе <strong>peek</strong> не вызовется)<br /><br />4) s.limit(5).forEach(System.out::println);<br /><br />завершает поток, годится для бесконечных потоков.',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (896,8,0,'У примитивных потоков есть метод <strong>average</strong>(), который возвращает среднее для значений потока. Является терминальной операцией. Возвращает <strong>OptionalDouble</strong>, у которого есть метод <strong>getAsDouble</strong>(), возвращающий значение <strong>double</strong> либо <strong>NoSuchElementException</strong>, если контейнер пуст.',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
