INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (901,8,2,'Укажите метод маппинга различных потоков (объектных и примитивных) в другие потоки<strong>.</strong>',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '2019-04-27 11:05:59');
