INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (918,8,2,'Что выдаст следующий код?<br /><br />List&lt;String&gt; cats = new ArrayList&lt;&gt;();<br />cats.add("Annie");<br />cats.add("Ripley");<br />Stream&lt;String&gt; stream = cats.stream();<br />cats.add("KC");<br />System.out.println(stream.count());',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (1898,918,'<strong>3</strong>. Потоки используют lazy evaluation, так что в строке <br />Stream&lt;String&gt; stream = cats.stream(); <br />полновесный поток реально не создается, а создается объект потока, который знает, куда смотреть, когда потребуются данные.',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (919,8,2,'Опишите методы <strong>map</strong>, <strong>filter</strong>, <strong>flatMap</strong> класса <strong>Optional</strong>',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '2019-04-27 12:23:11');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (6408,919,'В классе java.util.<strong>Optional</strong>&lt;<em>T</em>&gt; есть полезные методы:<br /><br />1) &lt;U&gt; Optional&lt;U&gt; <strong>map</strong>(Function&lt;? super T,? extends U&gt; mapper)<br /><br />мапит Optional в Optional потенциально другого типа.<br />Если результат функции null или если исходный контейнер пустой - будет пустой контейнер.<br />Если маппер null, то NullPointerException<br /><br />2) Optional&lt;T&gt; <strong>filter</strong>(Predicate&lt;? super T&gt; predicate)<br /><br />Проверяет содержимое контейнера на соответствие предикату. Если не соответствуют - возвращает пустой контейнер, если соответствует - возвращает контейнер со значением.<br /><br />3) &lt;U&gt; Optional&lt;U&gt; <strong>flatMap</strong>(Function&lt;? super T,Optional&lt;U&gt;&gt; mapper)<br /><br />Метод похож на map, но для мапинга используею функцию, которая возвращает Optional&lt;U&gt;, а не значение.',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (920,8,2,'Напишите метод, который принимает Optional&lt;Integer&gt; и печатает его содержимое, если в числе ровно три цифры.',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (1899,920,'void threeDigit(Optional&lt;Integer&gt; optional) {<br />&nbsp;&nbsp;&nbsp; optional.map(n -&gt; "" + n)<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; .filter(s -&gt; s.length() == 3)<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; .ifPresent(System.out::println);<br />}',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (921,8,0,'Методы, которые выбрасывают checked exceptions, нельзя использовать в лямбдах на основе стандартных функциональных интерфейсов Java, так как последние не могут продуцировать checked exceptions. В этом случае будет ошибка компиляции. Выходов два.<br />1) написать собственный функциональный интерфейс, декларирующий checked exceptions<br />2) написать метод-обертку, в котором checked exception будет преобразовываться в uncheched, и уже этот метод использовать в лямбде.',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (922,8,2,'Опишите коллекторы <strong>averagingDouble, </strong><strong><strong>averagingInt, </strong></strong><strong><strong><strong>averagingLong <br /></strong></strong></strong>',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '2019-04-27 12:45:59');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (6409,922,'В Collectors есть коллекторы<br />Collector&lt;T,?,Double&gt; <strong>averagingDouble</strong>(ToDoubleFunction&lt;? super T&gt; f), <br />Collector&lt;T,?,Double&gt; <strong>averagingInt</strong>(ToIntFunction&lt;? super T&gt; f), <br />Collector&lt;T,?,Double&gt; <strong>averagingLong</strong>(ToDoubleFunction&lt;? super T&gt; f)<br /><br />высчитывают среднее по итогам маппинга на примитив.<br /><br />Пример:<br />Stream&lt;String&gt; ohMy = Stream.of("lions", "tigers", "bears");<br />Double result = ohMy.collect(Collectors.averagingInt(String::length));',true,'');
