	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (6411,924,'<a>В Collectors есть коллекторы<br /><br />Collector&lt;CharSequence,?,String&gt; <strong>joining</strong>()<br />Collector&lt;CharSequence,?,String&gt; <strong>joining</strong>(CharSequence delimiter)<br /><br />конкатенация с разделителем или без. порядок сохраняется.<br /><br />Пример:<br /><br />Stream&lt;String&gt; ohMy = Stream.of("lions", "tigers", "bears");<br />String result = ohMy.collect(Collectors.joining(", "));<br /><br /></a>',true,'');
