	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (2037,984,'Epoch - это 01.01.1970. Кстати говоря, по Гринвичу<br /><br />- <strong>LocalDate</strong> имеет метод <strong>toEpochDay</strong>() - кол-во дней с момента Epoch.<br />- <strong>LocalDateTime</strong> и <strong>ZonedDateTime</strong> имеют метод <strong>toEpochSecond</strong>() - кол-во секунд с момента <em>Epoch</em>.<br />- <strong>LocalTime</strong> не имеет ни того, ни другого',true,'');
