INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (1013,8,2,'Что такое интернационализация?',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (2056,1013,'Интернационализация (<em>internatiolization</em>, <em>i18n</em>) - это элементы дизайна программы, позволяющие использовать ее с несколькими региональными настройками языка и форматов. При этом нет обязательного требования, чтобы система поддерживала более одного формата/языка, главное - иметь такую возможность.',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (1014,8,2,'Что такое локализация?',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (2057,1014,'Локализация (<em>localization</em>, <em>l10n</em>) - это поддержка нескольких локалей, т.е. "особых географических, политических или культурных регионов". Можно рассматривать локаль как пару "язык-страна". Локализация включает в себя перевод строк на другие языки, вывод дат и чисел в корректном формате',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (1015,8,0,'Класс <strong>Locale</strong> находится в пакете java.util.',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (1016,8,2,'Как получить локаль по умолчанию?',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (2058,1016,'Locale locale = Locale.getDefault(); // en_US',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (1017,8,0,'Локаль может включать (<strong>en_US</strong>), а может и не включать (<strong>fr</strong>) в себя страну.',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (1018,8,1,'Отметьте корректные наименования локалей.',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (2059,1018,'US',false,'нельзя без языка');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (2060,1018,'enUS',false,'отутствует подчеркивание между языком и страной');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (2061,1018,'US_en',false,'перепутаны местами страна и язык');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (2062,1018,'EN',false,'язык должен указаываться строчными буквами');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (2063,1018,'en',true,'локаль может быть без страны');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (2064,1018,'en_US',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (1019,8,2,'Укажите 3 способа получения объектов локалей.',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (2065,1019,'1) Через константу:<br />Locale.GERMANY<br /><br />2) через конструктор<br />new Locale("ru", "RU")<br />new Locale("ru")<br /><br />3) с использованием паттерна Builder<br />new Locale.Builder()<br />&nbsp; .setLanguage("en")<br />&nbsp; .setRegion("US")<br />&nbsp; .build();&nbsp;&nbsp;&nbsp;',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (1020,8,2,'Как <span style="text-decoration: underline;">не</span> надо использовать Locale.Builder()?',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (2066,1020,'Locale.Builder позволяет осуществлять манипуляции, которые в удущем могут принести проблемы.<br /><br />1) не надо создавать регионы строчными буквами, а языки - большими.<br /><br />Locale l2 = new Locale.Builder() // bad but legal<br />&nbsp; .setRegion("us")<br />&nbsp; .setLanguage("EN")<br />&nbsp; .build();&nbsp;&nbsp;&nbsp; <br /><br />2) не надо создавать пустые локали:<br />Locale l2 = new Locale.Builder().build(); // bad but legal',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (1021,8,2,'Как установить локаль по умолчанию и какие особенности у такого подхода?',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (2067,1021,'Locale locale = new Locale("fr");<br />Locale.setDefault(locale);<br /><br />Эта настройка применится только для текущей программы. Никаких изменений на системном уровне не произойдет. Используется такой подход очень редко.',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (1022,8,2,'Что такое resource bundle?',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (2068,1022,'Это набор локально-ориентированных объектов. Похоже на map с ключами и значениями. Может содержаться в Java-классе или в файле свойств (<em>property file</em>).',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (1023,8,0,'Файл свойств (<em>property file</em>) выглядит следующим образом:<br /><br />Название:<br /><br />&lt;SomeName&gt;_&lt;language&gt;[_&lt;country&gt;].properties, например:<br /><br />Zoo_ru.properties<br /><br />Содержимое:<br /><br />&lt;ключ&gt; = &lt;значение&gt;, например:<br /><br />hello=привет',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (1024,8,2,'Как обратиться к ресурсу из ресурсного файла?',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (2069,1024,'если ресурсный файл находится в пакете <strong>pack2</strong> и называется <strong>Zoo_ru.properties</strong>:<br /><br />import java.util.ResourceBundle<br />...<br /><br />ResourceBundle rb = ResourceBundle.getBundle("pack2/Zoo", new Locale("ru"));<br />System.out.println(rb.getString("hello"));<br /><br />Локаль должна точно соответствовать файлу свойств. Если в последнем указана страна, то локали только по языку недостаточно (файл не найдется)',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (1025,8,2,'Расскажите о формате файла свойств (property file):<br /><br />1) как хранятся пары ключ-значение<br />2) как задаются комментарии<br />3) как обрабатываются пробелы<br />4) для чего нужен обратный слеш в конце строки<br />5) как используются эскейп-последовательности',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (2070,1025,'1) тремя способами:<br />key=value<br />key:value<br />key value<br /><br /><br /><br />2) ! или # в начале строки<br /><br />3) Пробелы до и после разделителя игнорируются<br />Пробелы в начале строки игнорируются<br />Пробелы в конце строки <span style="text-decoration: underline;">не</span> игнорируются<br /><br />4) для переноса строки<br /><br />5) стандартно. Например, \n или \t',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (1026,8,0,'Если надо перенести длинную строку в коде, следует использовать конкатенацию:<br /><br />System.out.println("1 - Вход" + <br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; "2 - Регистрация" + <br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; "3 - Меню" + <br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; "4 - Поиск" + <br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; "5 - Сортировки" + <br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; "6 - Сохранение в файл" + <br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; "7 - Об авторе" + <br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; "8 - О приложении");',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (1027,8,2,'Как пробежаться по всем ресурсам в <strong>ResourceBundle</strong>?',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (2071,1027,'ResourceBundle rb = ResourceBundle.getBundle("pack2/Zoo", new Locale("ru", "RU"));<br />rb.keySet().stream().map(k -&gt; k+"-&gt;"+rb.getString(k)).forEach(System.out::println);',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (1028,8,0,'Есть класс java.util.<strong>Properties</strong>. Он может содержать в себе пары ключ-значение, а также предоставляет некоторые дополнительные возможности.',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (1029,8,2,'Как получить объект <strong>Properties</strong> из <strong>ResourceBundle</strong>?',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (2072,1029,'Properties props = new Properties();<br />rb.keySet().stream().forEach(k -&gt; props.put(k, rb.getString(k)));&nbsp;&nbsp;&nbsp;',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (1030,8,2,'1) Как получить ресурс из объекта <strong>Properties</strong>?<br /><br />2) Как получить значение по умолчанию, если ресурса нет?<br /><br />3) Какое место имеет <strong>Properties</strong> в иерархии классов?',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '2019-06-20 11:40:34');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (2073,1030,'Properties props = new Properties();<br /><br />1) System.out.println(props.getProperty("notReallyAProperty"));<br /><em>или</em><br />System.out.println(props.get("notReallyAProperty"));<br /><br />(если свойства нет, вернется null)<br /><br />2) System.out.println(props.getProperty("notReallyAProperty", "123"));<br /><br />кстати, метод <strong>getProperty</strong>() прнинимает и возвращает <em>String</em><br /><br />3) Наследует классу <strong>Hashtable</strong>&lt;<em>Object</em>, <em>Object</em>&gt; и имплементирует <strong>Map</strong>&lt;<em>Object</em>, <em>Object</em>&gt;',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (1031,8,2,'Как создать Java Class Resource Bundle и в чем его преимущества по сравнению с property file?',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '2019-06-20 11:07:24');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (2074,1031,'import java.util.*;<br />public class Zoo_ru_RU extends <strong>ListResourceBundle</strong> {<br />&nbsp;&nbsp;&nbsp; protected Object[][] getContents() {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return new Object[][] {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; { "hello", "Привет!" },<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; { "bye", "Пока" } <br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; };<br />&nbsp;&nbsp;&nbsp; } <br />}<br /><br />Преимущества:<br />1) значениями свойств могут быть не только строки, но также и любые объекты.<br />2) можно создавать свойства в рантайме<br /><br />Важно! при использовании ресурсного класса <strong>getContents</strong>() на экземпляре по факту вызывается <span style="text-decoration: underline;">единожды</span>',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (1032,8,2,'Как получить ресурсы из ресурсного класса (Java Class Resource Bundle)?',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (2075,1032,'если ресурсный файл-класс находится в пакете <strong>pack2</strong> и называется <strong>Zoo_ru_RU</strong>:<br /><br /><br />import java.util.ResourceBundle<br />...<br />ResourceBundle rb2 = ResourceBundle.getBundle("pack2.Zoo", new Locale("ru", "RU"));<br />System.out.println(rb2.<strong>getObject</strong>("bye"));<br /><br />Локаль должна точно соответствовать классу (если в классе указана страна, одного языка в локали недостаточно).',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (1033,8,0,'Методы <strong>getObject</strong>() и <strong>getString</strong>() класса <strong>ResourceBundle</strong> выдают <strong>MissingResourceException</strong>, если ресурса нет.',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (1034,8,0,'Доступ к файлу свойств или ресурсному классу только по имени:<br /><br />ResourceBundle.getBundle("SomeName", someLocale)<br /><br />возможен только если он находится в default package',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (1035,8,0,'Методом ResourceBundle.<strong>getBundle</strong>("name") можно загрузить ресурсы для дефолтной локали.',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (1036,8,0,'Если мы запрашиваем ResourceBundle из property file явным образом (слеши вместо точек в пути, getBundle("pack2/Zoo"), то такой файл должен быть, иначе никакого подбора подходящих ресурсов не будет. Если пакет указан с точками, то будет перебор подходящих.<br /><br />Еще. Если у нас локаль со страной, то к ней подойдут ресурсы только с языком, но если локаль только с языком, то ресурсы со страной не подойдут.',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (1037,8,2,'Укажите приоритеты подбора ресурсов при запросе ResourceBundle из класса ресурсов c полной локалью.<br /><br />ResourceBundle.getBundle("pack2.Zoo", new Locale("ru", "RU"))',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (2076,1037,'1) Zoo_ru_RU.java<br />2) Zoo_ru_RU.properties<br />3) Zoo_ru.java<br />4) Zoo_ru.properties<br />5) Zoo_en_US.java (дефолтная локаль)<br />6) Zoo_en_US.properties<br />7) Zoo_en.java<br />8) Zoo_en.properties<br />9) Zoo.java (дефолтные ресурсы)<br />10) Zoo.properties<br />если ничего не нашлось - MissingResourceException',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (1038,8,0,'Когда речь пойдет о ResourceBundle, то, вероятно, не будет указываться путь к пакету, т.е. вся логика пройдет как бы в default package:<br /><br />ResourceBundle.getBundle("Zoo", new Locale("en"));',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (1039,8,2,'Укажите приоритеты подбора ресурсов при запросе ResourceBundle из класса ресурсов c неполной локалью (только язык) .<br /><br />ResourceBundle.getBundle("pack2.Zoo", new Locale("ru"))',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (2077,1039,'1) Zoo_ru.java<br />2) Zoo_ru.properties<br />3) Zoo_en.java (язык дефолтной локали)<br />4) Zoo_en.properties<br />5) Zoo.java (дефолтные ресурсы)<br />6) Zoo.properties<br />если ничего не нашлось - MissingResourceException<br /><br />т.е. ресурсы с указанием страны в этом случае не просматриваются вообще',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (1040,8,0,'Если в найденном <strong>ResourceBundle</strong> не находится запрашиваемый ключ, то производится попытка получить значение ключа из ресурса, вышестоящего в иерархии. <span style="text-decoration: underline;">Выше по иерархии</span> - это значит отбрасывать по одному специализацию: Zoo_fr_FR.java -&gt; Zoo_fr.java -&gt; Zoo.java <br /><br />Причем действует правило: если совпадают ресурсный класс и файл свойств, то файл свойств выпадает из зоны видимости.',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (1041,8,0,'В Java-ресурсах значения строк часто идут со ссылкой на возможные вставки, например<br /><br />helloByName=Hello, {0}<br /><br />Потом в коде программы можно будет что-нибудь подставить:<br /><br />String format = rb.getString("helloByName");<br />String formatted = MessageFormat.format(format, "Tammy");<br />System.out.print(formatted);',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (1042,8,2,'Есть класс java.text.<strong>NumberFormat</strong>,&nbsp; который обладает методами-фабриками для получения разнообразных форматтеров. Перечислите эти методы и опишите, что они возвращают.',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (2078,1042,'1) <em>NumberFormat</em> <strong>getInstance</strong>() - форматтер общего назначения<br /><em>NumberFormat</em> <strong>getInstance</strong>(<strong>Locale</strong> <em>locale</em>)<br /><br />2) <em>NumberFormat</em> <strong>getNumberInstance</strong>() - аналогично<br /><em>NumberFormat</em> <strong>getNumberInstance</strong>(<strong>Locale</strong> <em>locale</em>)<br /><br />3) <em>NumberFormat</em> <strong>getCurrencyInstance</strong>() - для форматирования валют<br /><em>NumberFormat</em> <strong>getCurrencyInstance</strong>(<strong>Locale</strong> <em>locale</em>)<br /><br />4) <em>NumberFormat</em> <strong>getPercentInstance</strong>() - для форматирования процентов<br /><em>NumberFormat</em> <strong>getPercentInstance</strong>(<strong>Locale</strong> <em>locale</em>)<br /><br />5) <em>NumberFormat</em> <strong>getIntegerInstance</strong>() - для округления десятичных значений<br /><em>NumberFormat</em> <strong>getIntegerInstance</strong>(<strong>Locale</strong> <em>locale</em>)',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (1043,8,0,'Любой экземпляр <strong>NumberFormat</strong> умеет парсить строку:<br /><br /><em>Number</em> <strong>parse</strong>(<strong>String</strong> <em>source</em>)<br /><br />и форматировать число в строку:<br /><br /><em>String</em> <strong>format</strong>(<em>много перезагрузок</em>)<br /><br />Стиль форматирования и парсинга определяется локалью:<br /><br />NumberFormat en = NumberFormat.getInstance(Locale.US);<br />NumberFormat fr = NumberFormat.getInstance(Locale.FRANCE);<br />String s = "40.45";<br />System.out.println(en.parse(s)); // 40.45<br />System.out.println(fr.parse(s)); // 40<br /><br />Выдает checked exception <strong>ParseException</strong>, если парсинг не получился, так что надо обязательно проверять обработку исключения, анализируя задачи экзамена. Парсинг начинается с начала строки и как только встречает символ, который не может распарсить, прекращается и возвращает то, что имеет к этому моменту. Если к этому моменту ничего еще нет - то <strong>ParseException</strong>.',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (1044,8,0,'Объекты NumberFormat - не являются thread-safe, так что не надо хранить их в переменных класса и экземпляра.',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (1045,8,2,'Отформатируйте форматтером <br />1) целое число 3 200 000<br />2) вещественное число 48 как валюту',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (2079,1045,'1) int num = 3_200_000;<br />NumberFormat nf = NumberFormat.getInstance();<br />System.out.println(nf.format(num));<br /><br />2) double cur = 48;<br />NumberFormat nf = NumberFormat.getCurrencyInstance();<br />System.out.println(nf.format(cur));',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (1046,8,0,'Бывает, что существуют сразу две дефолтных локали:<br /><br />Locale.getDefault(Locale.Category.FORMAT);<br />Locale.getDefault(Locale.Category.DISPLAY);<br />',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (1047,8,1,'Дан код:<br /><br />NumberFormat nf = NumberFormat.getInstance(Locale.US);<br />String str = _______;<br />System.out.println(nf.parse(str));<br /><br />Какие варианты строки не приведут к ParseExcetion?',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (2080,1047,'456abc',true,'будет 456');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (2081,1047,'-2.5165x10',true,'будет -2.5165');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (2082,1047,'x85.3',false,'парсинг невозможен с самого начала строки');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (2083,1047,'3,200',true,'будет 3');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (1048,8,2,'Приведите пример парсинга валютной строки.',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (2084,1048,'String amt = "95,55 руб.";<br />NumberFormat cf = NumberFormat.getCurrencyInstance(new Locale("ru", "RU"));<br />try {<br />&nbsp;&nbsp;&nbsp; Number num = cf.parse(amt);<br />&nbsp;&nbsp;&nbsp; double value = num.doubleValue();<br />&nbsp;&nbsp;&nbsp; System.out.println(value);&nbsp;&nbsp; <br />} catch (ParseException pe) {<br />&nbsp;&nbsp;&nbsp; pe.printStackTrace();<br />}',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (1049,8,0,'Есть такой класс java.time.<strong>DateTimeFormatter</strong>, который позволяет форматировать дату и время - любой объект.',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (1050,8,2,'Отформатируйте по ISO дату 22.03.1980, время 00:05, таймстемп 22.03.1980 00:05',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (2085,1050,'LocalDate date = LocalDate.of(1980,3,22);<br />LocalTime time = LocalTime.of(0,5);<br />LocalDateTime dateTime = LocalDateTime.of(date, time);<br />System.out.println(date.format(DateTimeFormatter.ISO_LOCAL_DATE));<br />System.out.println(time.format(DateTimeFormatter.ISO_LOCAL_TIME));<br />System.out.println(dateTime.format(DateTimeFormatter.ISO_LOCAL_DATE_TIME));<br /><br />--<br />1980-03-22<br />00:05:00<br />1980-03-22T00:05:00<br /><br />Если форматтер даты/времени не подходит, буде RTE',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (1051,8,0,'У класса java.time.<strong>DateTimeFormatter</strong> есть методы:<br /><br /><em>DateTimeFormatter </em><strong>ofLocalizedDate</strong>(<strong>FormatStyle</strong> <em>dateStyle</em>)<br /><em>DateTimeFormatter </em><strong>ofLocalizedTime</strong>(<strong>FormatStyle</strong> <em>timeStyle</em>)<br /><em>DateTimeFormatter </em><strong>ofLocalizedDateTime</strong>(<strong>FormatStyle</strong> <em>dateTimeStyle</em>)<br /><em>DateTimeFormatter </em><strong>ofLocalizedDateTime</strong>(<strong>FormatStyle</strong> <em>dateStyle</em>, <strong>FormatStyle </strong><em>timeStyle</em>)<br />&nbsp;Самый популярные стили формата - <strong>FormatStyle.SHORT </strong>и<strong> FormatStyle.MEDIUM<br /></strong>',TIMESTAMP '1970-01-01 03:00:00',TIMESTAMP '1970-01-01 03:00:00');
