INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (1247,8,0,'Атомарные обертки реализованы только для наиболее часто используемых примитивов. Целочисленные не-Integer примитивы можно хранить в <strong>AtomicInteger</strong>. <br /><br /><strong>float</strong> можно хранить в <strong>AtomicInteger</strong> с использованием метода <em>Float</em>.<strong>floatToIntBits</strong>(), а <strong>double</strong> - в <strong>AtomicLong</strong> с использованием <em>Double</em>.<strong>doubleToLongBits</strong>().',TIMESTAMP '2019-02-26 18:28:00',TIMESTAMP '2019-02-26 18:28:19');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (1248,8,2,'Что такое упорядоченный/неупорядоченный поток?',TIMESTAMP '2019-02-27 12:14:40',TIMESTAMP '2019-02-27 12:14:52');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (2417,1248,'Потоки могут иметь или не иметь порядок отправки элементов в обработку (<em>encounter order</em>).<br />Это свойство зависит <span style="text-decoration: underline;">от источника данных и от промежуточных операций</span>. Например, списки и массивы генерируют упорядоченные потоки, а сеты - нет. Промежуточная операция <strong>sorted</strong>() может сделать поток упорядоченным, а метод <strong>unordered</strong>() может сделать поток неупорядоченным.<br />Для последовательного потока упорядоченность/неупорядоченность не влияет на быстродействие (хотя может влиять на консистентность результата), а в параллельной обработке для неупорядоченных потоков резко возрастает быстродействие.',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (1249,8,0,'Пример использования <strong>RecursiveTask</strong>:<br /><br />public class WeighAnimalTask extends RecursiveTask&lt;Double&gt; {<br />&nbsp;&nbsp;&nbsp; private int start;<br />&nbsp;&nbsp;&nbsp; private int end;<br />&nbsp;&nbsp;&nbsp; private Double[] weights;<br />&nbsp;&nbsp;&nbsp; public WeighAnimalTask(Double[] weights, int start, int end) {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; this.start = start;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; this.end = end;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; this.weights = weights;<br />&nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp; protected Double compute() {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if(end-start &lt;= 3) {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; double sum = 0;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; for(int i=start; i&lt;end; i++) {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; weights[i] = (double)new Random().nextInt(100);<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; System.out.println("Animal Weighed: "+i);<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; sum += weights[i];<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return sum;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; } else {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; int middle = start+((end-start)/2);<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; System.out.println("[start="+start+",middle="+middle+",end="+end+"]");<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; RecursiveTask&lt;Double&gt; otherTask = new WeighAnimalTask(weights,start,middle);<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; otherTask.fork();<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return new WeighAnimalTask(weights,middle,end).compute() + otherTask.join();<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp; }<br />}',TIMESTAMP '2019-02-27 12:54:03',TIMESTAMP '2019-02-27 12:54:20');
